INSERT INTO `proper__concludingprayer` (`dayID`, `Content`) VALUES
('_Virgin~Many-0V', '=_Virgin~Many-1L'),
('_Virgin~Many-1L', 'கருணையின் ஊற்றாகிய கடவளே, எங்கள் மீது உமது அன்பையும் இரக்கத்தையும் நிறைவாகப் பொழிந்தருளும்; கன்னியரான …………………… என்னும் புனிதர்களின் திருவிழாவைப் பக்தி வணக்கத்துடன் கொண்டாடி அக்களிக்கின்ற நாங்கள், உமது அருளின் கொடையினால் இவர்களுடன் முடிவில்லாத் தோழமை கொண்டு இன்புற்றிருக்க அருள்புரியும். உம்மோடு…'),
('_Virgin~Many-5V', '=_Virgin~Many-1L'),
('_Virgin~One-0V', '=_Virgin~One-1L'),
('_Virgin~One-1L', 'ஆண்டவராகிய கடவுளே உம் மகனோடு வந்து உம்மை அன்பு செய்பவர்களின் உள்ளங்களில் உம் உறைவிடத்தை அமைப்பதாக நீர் அவர்களுக்குகு வாக்களிக்கிறீர். உமது கன்னி ……………………. ன் பரிந்துரையின்படி, நீர் உமது தூய்மைப்படுத்தும் அருளோடு வருகை தந்து, நீர் குடிகொள்ளும் ஓர் இடமாக எங்கள் இதயங்களை மாற்றியருளும். உம்மோடு…§{அல்லது}§அருள் வழங்கும் வள்ளலாகிய ஆண்டவரே கன்னியான புனித ……………. உடைய புண்ணிய வாழ்வை பக்தியுடன் நினைவுகூர்கின்ற நாங்கள், உம்மீது கொண்ட அன்பில் நிலைத்து நிற்கவும் இறுதிவரை அதே அன்பில் வளரவும் எங்களுக்கு அருள்வீராக. உம்மோடு…'),
('_Virgin~One-5V', '=_Virgin~One-1L'),
('All Saints-0V', '=All Saints-1L'),
('All Saints-1L', 'என்றும் வாழும் எல்லாம் வல்ல கடவுளே! எல்லாப் புனிதர்களிலும் நீர் கொண்டிருந்த உம் அருளின் வெற்றியினை மகிழ்வோடு நாங்கள் கொண்டாம கொண்டிருக்கிறோம். எங்களுக்காகப் பரிந்து பேச புனிதர்களின் பெரும் எண்ணிக்கையை முன்னிட்டு, நாங்கள் எப்போதும் ஆவலோடு கெஞ்சிக் கேட்கும் உமது இரக்கப் பெருக்கை உம்மிடமிருந்து பெற்றுக் கொள்வோமாக. உம்மோடு…'),
('All Saints-5V', '=All Saints-1L'),
('All Souls-1L', 'என்றும் வாழும் எல்லாம் வல்ல வேண்டுதல்களுக்குத் தயவுடன் செவிசாய்த்தருளும், உம் கடவுளே! எங்கள் இறந்தோரிடமிருந்து உயிர்ப்பிக்கப்பெற்றார் மகன் என்னும் எங்கள் நம்பிக்கை எழுச்சியுறுவதுபோல இறந்த உம் அடியார்கள் உயிர்த்தொழுவார்கள் என்னும் எங்கள் நம்பிக்கையும் உறுதிபெறுவதாக. உம்மோடு…'),
('Birth of the Blessed Virgin Mary-1L', 'ஆண்டவராம் கடவுளே! பேறுபெற்ற கன்னிமரியா உம் மகனைப் பெற்றெடுத்தபோது எங்கள் மீட்பின் நாள் உதயமானது. அவருடைய பிறப்பு நாளைக் கொண்டாடும் எங்களுக்கு உமது அருளையும் அமைதியையும் வழங்குவீராக. உம்மோடு…'),
('Birth of the Blessed Virgin Mary-5V', '=Birth of the Blessed Virgin Mary-1L'),
('Exaltation of the Holy Cross-0V', '=Exaltation of the Holy Cross-1L'),
('Exaltation of the Holy Cross-1L', 'எங்கள் தந்தையாம் கடவுளே! உமது விருப்பத்தை நிறைவேற்ற உம் ஒரே மகன் எங்களுடைய மீட்புக்காக லுவையின் பாடுகளைத் தாங்கினார். அச்சிலுவையின் மறைபொருளை இவ்வுலகில் அறிந்திருக்கும் நாங்கள் அது ளிக்கும் நிலைவாழ்வாகிய பரிசை, மறுமையில் பெற்றுக் கொள்வாமாக. உம்மோடு…'),
('Exaltation of the Holy Cross-5V', '=Exaltation of the Holy Cross-0V'),
('Guardian Angels-1L', 'விண்ணகத் தாதரணிகளின் ஆண்டவராகிய கடவுளே! உமது ஞானமிகு பராமரிப்பினால் எங்களை வழிநடத்திப் பாதுகாக்க உம் தூதர்களை அனுப்புகிறீர்: நாங்கள் என்றும் அவர்களுடைய அக்கறைமிகு பாதுகாவலைப் பெறவும் விண்ணகத்தில் அவர்களுடைய தோழமையின் பங்கு பெறவும் செய்தருளும். உம்மோடு…'),
('Guardian Angels-5V', '=Guardian Angels-1L'),
('IN Saint Teresa of Calcutta, virgin-1L', 'அருள் வழங்கும் வள்ளலாகிய ஆண்டவரே கன்னியான புனித அன்னை தெரேசாவின் புண்ணிய வாழ்வை பக்தியுடன் நினைவுகூர்கின்ற நாங்கள், உம்மீது கொண்ட அன்பில் நிலைத்து நிற்கவும் இறுதிவரை அதே அன்பில் வளரவும் எங்களுக்கு அருள்வீராக. உம்மோடு…'),
('IN Saint Teresa of Calcutta, virgin-5V', '=IN Saint Teresa of Calcutta, virgin-1L'),
('Our Lady of Sorrows-1L', 'எங்கள் தந்தையாம் கடவுளே! உம் மகன் சிலுவையில் உயர்த்தப்பட்டபோது, அவருடைய அன்னை மரியாவும் அங்கு நின்று தம் உள்ளத்தில் அவரோடு துயருறத் திருவுளமானீர்; அந்த அன்னயோடு ஒன்றிணைந்து, உமது திருச்சபை கிறிஸ்துவின் பாடுகளில் பங்கு கொள்ளவும் இவ்வாறு, அவரது உயிர்ப்பின் மாட்சிமைக்கு வந்து சேரவும் அருள்வீராக,'),
('Our Lady of Sorrows-5V', '=Our Lady of Sorrows-1L'),
('Our Lady of the Rosary-1L', 'ஆண்டவரே உமது அருளுக்கு எங்கள் உள்ளங்களைத் திறந்தருளும். உம் மகன் கிறிஸ்து, மானிட உருவானதை தூதர்களின் நற்செய்தி வழியாக அறிந்து நம்பியிருக்கிற நாங்கள், பேறுபெற்ற கன்னிமரியாவின் பரிந்துரையினால் கிறிஸ்துவின் சிலுவைப்பாடுகளின் வழியாக அவரது உயிர்ப்பின் மாட்சிக்கு வந்து சேர்வோமாக.'),
('Our Lady of the Rosary-5V', '=Our Lady of the Rosary-1L'),
('OW02-0Sun-0V', '=OW02-0Sun-1L'),
('OW02-0Sun-1L', 'என்றும் வாழும் எல்லாம் வல்ல கடவுளே! மண்ணகத்திலும் விண்ணகத்திலும் உள்ள அனைத்தையும் ஆள்பவர் நீர். உம் மக்களின் மன்றாட்டுகளுக்குக் கனிவுடன் செவிசாய்த்து, எங்கள் வாழ்நாளில் உமது அமைதியை அளித்தருளும். உம்மோடு…'),
('OW02-0Sun-5V', '=OW02-0Sun-1L'),
('OW03-0Sun-0V', '=OW03-0Sun-1L'),
('OW03-0Sun-1L', 'என்றும் வாழும் எல்லாம் வல்ல கடவுளே! உமது அன்பின் வழியில் எங்களை நடத்தியருளும்; இவ்வாறு அன்புக்குரிய உம் மகன் இயேசுவின் பெயரால், நாங்கள் செய்வது அனைத்திலும் எங்கள் வாழ்க்கை முழுவதும் மணம் பரப்புவதாக. உம்மோடு…'),
('OW03-0Sun-5V', '=OW03-0Sun-1L'),
('OW04-0Sun-0V', '=OW04-0Sun-1L'),
('OW04-0Sun-1L', 'எங்கள் கடவுளாகிய ஆண்டவரே, எல்லாவற்றிற்கும் மேலாக உம்மையும் உமக்குகந்த அன்பினால் எம் சகோதரர் சகோதரிகளையும் அன்பு செய்ய வரம் அருள்வீராக. உம்மோடு…'),
('OW04-0Sun-5V', '=OW04-0Sun-1L'),
('OW05-0Sun-0V', '=OW05-0Sun-1L'),
('OW05-0Sun-1L', 'ஆண்டவரே! இடைவிடாத பரிவிரக்கத்தோடு, உமது குடும்பத்தைப் பாதுகாத்தருளும்; ஏனெனில், உமது தூய அருளில் மட்டுமே நாங்கள் நம்பிக்கை கொள்கிறோம. உம்மோடு…'),
('OW05-0Sun-5V', '=OW05-0Sun-1L'),
('OW06-0Sun-0V', '=OW06-0Sun-1L'),
('OW06-0Sun-1L', 'கருணைக் கடலாகிய கடவுளே! உம் மகனோடு வந்து நீர் உம் இல்லத்தை உம்மை அன்பு செய்பவர்கள் உள்ளத்தில் அமைப்பதாக நீர் அவர்களுக்கு வாக்களிக்கிறீர். நீர் தூய்மைப்படுத்தும் உம் அருளினால் எங்கள் உள்ளத்தை நீர் தங்குவதற்கேற்ற ஓர் இல்லிடமாக மாற்றியருளும், உம்மோடு…'),
('OW06-0Sun-5V', '=OW06-0Sun-1L'),
('OW07-0Sun-0V', '=OW07-0Sun-1L'),
('OW07-0Sun-1L', 'எல்லாம் வல்ல கடவுளே! உமது ஆவிக்குரியவற்றில் நாங்கள் என்றும் கருத்தூன்றி நின்று உமக்கு உகந்தவற்றையே சொல்லாலும் செயலாலும் நிறைவேற்ற எங்களுக்கு அருள் புரியும். உம்மோடு…'),
('OW07-0Sun-5V', '=OW07-0Sun-1L'),
('OW08-0Sun-0V', '=OW08-0Sun-1L'),
('OW08-0Sun-1L', 'அன்போடு எம்மை ஆதரிக்கும் ஆண்டவரே! உம் இரக்கத்தினால், அமைதியை நாடுகின்ற மனிதரின் செயல்களை நெறிப்படுத்தியருளும். இதனால் உமது திருச்சபை, அமைதியிலும் மகிழ்ச்சியிலும் உமக்கு ஊழியம் செய்வதாக. உம்மோடு…'),
('OW08-0Sun-5V', '=OW08-0Sun-1L'),
('OW09-0Sun-0V', '=OW09-0Sun-1L'),
('OW09-0Sun-1L', 'ஆண்டவராகிய கடவுளே! தவறா மனத்தினாலும் அன்பினாலும் எங்கள் வாழ்வை நீர் ஆண்டு நடத்துகின்றீர் தீயவை அனைத்தையும் எங்களிடமிருந்து விலக்கி எங்களுக்குப் பயன்படும் அனைத்தையும் தந்தருள்வீராக. உம்மோடு…'),
('OW09-0Sun-5V', '=OW09-0Sun-1L'),
('OW10-0Sun-0V', '=OW10-0Sun-1L'),
('OW10-0Sun-1L', 'எங்கள் ஆண்டவராகிய கடவுளே! நலன்களுக்கெல்லாம் பிறப்பிடம் நீரே; உம்மைக் கெஞ்சி மன்றாடும் எங்களுக்கு இரங்கி, உமது ஏவுதலால் நாங்கள் சரியானவற்றை உணரவும், உமது பராமரிப்பால் அவற்றையே நிறைவேற்றவும் எங்களுக்குத் துணைை புரிவீராக. உம்மோடு…'),
('OW10-0Sun-5V', '=OW10-0Sun-1L'),
('OW11-0Sun-0V', '=OW11-0Sun-1L'),
('OW11-0Sun-1L', 'நம்பினோர்க்கு மனத்திடன் அளிக்கும் ஆண்டவராகிய கடவுளே! எங்கள் வேண்டுதலுக்குக் கனிவாய்ச் செவிசாய்த்தருளும்; உமது உதவியின்றி வலுவற்ற எங்களால் ஆவது ஒன்றுமில்லை; எனவே எங்கள் மனத்தாலும் செயலாலும் உம்முடைய கட்டளைகளை நிறைவேற்றி நாங்கள் உமக்கு உகந்தவர்களாய் வாழுமாறு எங்களுக்கு உம் அருள் உதவியை எப்போதும் தந்தருள்வீராக. உம்மோடு…'),
('OW11-0Sun-5V', '=OW11-0Sun-1L'),
('OW12-0Sun-0V', '=OW12-0Sun-1L'),
('OW12-0Sun-1L', 'தூயவராம் ஆண்டவரே! உமது அன்பினால் நீர் ஏற்படுத்தும் மக்களிடமிருந்து, உமது வழி நடத்தும் கைகளை நீர் ஒருபோதும் விலக்கிக் கொள்வதில்லை. ஆகவே உம் திருப்பெயருக்கு எப்போதும் பயந்து நடக்கவும் உம் திருப்பெயரை அன்பு செய்யவும் எங்களுக்குக் கற்றுத்தாரும். உம்மோடு…'),
('OW12-0Sun-5V', '=OW12-0Sun-1L'),
('OW13-0Sun-0V', '=OW13-0Sun-1L'),
('OW13-0Sun-1L', 'பேரருள் சுடராம் ஆண்டவராகிய கடவுளே! தம் பிள்ளைகளாக்கும் அருளினால், நீர் எங்களை ஒளியின் பிள்ளைகளாக உருவாக்கினீர்; உண்மையின் ஒளியில் நாங்கள் எப்போதும் வாழும் பொருட்டு தவறென்னும் இருளில் நாங்கள் சிக்கிக் கொள்ளாமல் இருக்கவும் உண்மையின் பேரொளிகாண்டு என்றும் உமது ஒளி எங்களில் சுடர்விடவும் அருள் கூர்ந்தருளும். உம்மோடு…'),
('OW13-0Sun-5V', '=OW13-0Sun-1L'),
('OW14-0Sun-0V', '=OW14-0Sun-1L'),
('OW14-0Sun-1L', 'எல்லாம் வல்ல ஆண்டவராகிய கடவுளே! சீரழிந்த எங்கள் உலகத்தை, உம் மகனின் பாடுகளினாலும் இறப்பினாலும் நீர் சீர்ப்படுத்தினீர்; அவர் எங்களுக்காகப் பெற்றுத்தந்த பாவ விடுதலையில் மகிழ்ந்திருக்க அருளைத் தந்தருளும். மேலும் எங்களை நிலையாயுள்ள பேரின்பத்திற்குக் கொண்டு வந்தருளும், உம்மோடு…'),
('OW14-0Sun-5V', '=OW14-0Sun-1L'),
('OW15-0Sun-0V', '=OW15-0Sun-1L'),
('OW15-0Sun-1L', 'இரக்கத்தின் ஊற்றாகிய தந்தையாம் கடவுளே! தவறி நடப்போருக்கு அவர்கள் நன்னெறிக்குத் திரும்பிவர நீர் அவர்களைத் தூண்டுகிறீர்; உண்மையின் ஒளியைக் காட்டி, திருமுழுக்கு அருளைப் பெற்றவர் அனைவரும் கிறிஸ்தவ அழைப்புக்கேற்ற தகுதியோடு தம் பெயருக்குப் பொருந்தாத வற்றை வெறுத்து ஒதுக்கி வாழ முனைவார்களாக. உம்மோடு…'),
('OW15-0Sun-5V', '=OW15-0Sun-1L'),
('OW16-0Sun-0V', '=OW16-0Sun-1L'),
('OW16-0Sun-1L', 'பரிவன்புமிக்க ஆண்டவரே! உமக்கு ஊழியம் புரிகின்ற எங்கள்மேல் மனமிரங்கி, உம் அருள் கொடைகளை உமது அன்பினால் எங்களில் வளம்பெறச் செய்வீராக. இவ்வாறு நாங்கள் நம்பிக்கை, எதிர்நோக்கு இறை அன்பினால் பற்றியெரிந்து, உம் கட்டளைகளைக் கடைபிடிப்பதில் கண்ணும் கருத்துமாய் நிலைத்திருக்கச் செய்தருளும். உம்மோடு…'),
('OW16-0Sun-5V', '=OW16-0Sun-1L'),
('OW17-0Sun-0V', '=OW17-0Sun-1L'),
('OW17-0Sun-1L', 'நம்பினோரைக் காத்துவரும் ஆண்டவராகிய கடவுளே! உம்மாலன்றி, நேர்மையானதும் தூய்மையானதும் எதுவுமில்லை: எனவே உம் அன்பினால் எங்களை எப்போதும் ஆதரித்தருளும் நிலையான நன்மைகளை நாங்கள் இப்பொழுதுகூட பற்றிக்கொள்ளும் பொருட்டு இவ்வுலகிலுள்ள நல்லவற்றைப் பயன்படுத்த எங்களுக்கு வழிகாட்டியருளும். உம்மோடு…'),
('OW17-0Sun-5V', '=OW17-0Sun-1L'),
('OW18-0Sun-0V', '=OW18-0Sun-1L'),
('OW18-0Sun-1L', 'எங்கள் ஆண்டவராகிய கடவுளே! நீர் எங்களைப் படைத்தீர் என்றும், உமது பராமரிப்பினால் எங்களை வழிநடத்துகிறீர் என்றும் நாங்கள் மகிழ்ச்சியோடு அறிந்து கொள்கிறோம். உமது தவறாப் பேரன்பினால் எங்கள் வேண்டலில், எங்களை ஆதரித்தருளும். உமது வாழ்வை எங்களில் புதுப்பித்து அதைக் காத்து, என்றென்றைக்கும் அது கனிகளை விளைவிக்கச் செய்தருளும். உம்மோடு…'),
('OW18-0Sun-5V', '=OW18-0Sun-1L'),
('OW19-0Sun-0V', '=OW19-0Sun-1L'),
('OW19-0Sun-1L', 'என்றும் வாழும் எல்லாம் வல்ல கடவுளே! நாங்கள் உம்மைத் தந்தை என்றும் ஆண்டவர் என்றும் நம்பிக்கையோடு அழைக்கிறோம்; நீர் எங்களுக்கு வாக்களித்த உரிமைச் சொத்தாகிய வான்வீட்டில், நாங்கள் வந்து சேரும்படி எங்கள் உள்ளங்களில் உமது ஆவியைப் புதுப்பித்து, உம் பிள்ளைகளாக எங்களை முழுமையாக மாற்றியருளும். உம்மோடு…'),
('OW19-0Sun-5V', '=OW19-0Sun-1L'),
('OW20-0Sun-0V', '=OW20-0Sun-1L'),
('OW20-0Sun-1L', 'ஆண்டவராகிய கடவுளே! உம்மை அன்பு செய்வோர்க்கு, கண்ணால் கண்டிராத, காதால் கேட்டிராத கொடையை நீர் ஏற்பாடு செய்திருக்கிறீர். உமது அன்பினால் எங்கள் உள்ளங்களை நிரப்பியருளும். இதன் வழியாக, நாங்கள் உம்மை அனைத்திலும் அனைத்திற்கும் மேலாக அன்பு செய்து, மனித உள்ளம் சிந்தித்திராத உம்முடைய வாக்குறுதிகளை நாங்கள் பெற்றுக் கொள்வோமாக. உம்மோடு…'),
('OW20-0Sun-5V', '=OW20-0Sun-1L'),
('OW21-0Sun-0V', '=OW21-0Sun-1L'),
('OW21-0Sun-1L', 'ஆண்டவரே! உம் அருளினால் நாங்கள் மனத்திலும் உள்ளத்திலும் ஒன்றாய் இருக்கிறோம். நீர் கற்பிப்பதை நாங்கள் நேசிக்கவும் நீர் வாக்களிப்பதை ஆர்வமுடன் விரும்பவும் செய்தருளும்; நாளுக்கு நாள் மாறிக் கொண்டிருக்கும் இவ்வுலகில், எங்கள் உள்ளம் மெய்யான பேரின்ப வீட்டையே நாட எங்களுக்கு அருள்வீராக. உம்மோடு…'),
('OW21-0Sun-5V', '=OW21-0Sun-1L'),
('OW22-0Sun-0V', '=OW22-0Sun-1L'),
('OW22-0Sun-1L', 'ஆற்றலும் வல்லமையும் மிக்க தந்தையே! நல்ல, சிறந்த கொடைகள் அனைத்தும் உம்மிடமிருந்தே எங்களுக்கு வருகின்றன. உமது பெயருக்கேற்ற அன்பை எங்கள் உள்ளங்களில் ஊன்றியருளும். உமது ஊழியத்தின் மேலுள்ள எங்கள் ஆவலைப் பெருகச் செய்து, எங்களில் நன்மையானவற்றை வளப்படுத்தி, அதை என்றும் காத்தருள்வீராக. உம்மோடு…'),
('OW22-0Sun-5V', '=OW22-0Sun-1L'),
('OW23-0Sun-0V', '=OW23-0Sun-1L'),
('OW23-0Sun-1L', 'அனைத்தையும் ஆண்டு நடத்தும் இறைவா! உமது அருளினால் எங்களை உம்முடைய சொந்த மக்களாகவும் ஆக்கினீர்; கிறிஸ்துவில் நம்பிக்கை கொண்டுள்ள உமகு அன்புக்குரிய எங்களை, நீர் கருணையுடன் கண்ணோக்கி எங்களுக்கு உண்மையான சுதந்திர வாழ்வையும் முடிவில்லா பேரின்பத்தையும் அளித்தருளும். உம்மோடு…'),
('OW23-0Sun-5V', '=OW23-0Sun-1L'),
('OW24-0Sun-0V', '=OW24-0Sun-1L'),
('OW24-0Sun-1L', 'அனைத்தையும் படைத்து ஆண்டு வரும் கடவுளே! எங்களைக் கனிவுடன் கண்ணோக்கியருளும்; எங்கள் முழு உள்ளத்துடன் உமக்கு பணி செய்யவும் உம்முடைய மன்னிப்பு, அன்பு இவற்றின் வல்லமையை உய்த்துணரவும் எங்களுக்கு அருள் தாரும். உம்மோடு…'),
('OW24-0Sun-5V', '=OW24-0Sun-1L'),
('OW25-0Sun-0V', '=OW25-0Sun-1L'),
('OW25-0Sun-1L', 'அமைதியை அருள்பவரும் அன்பினை விரும்புபவருமான தந்தையே! உம்மை அன்பு செய்வதிலும் எமக்கு அடுத்திருப்பவரிடம் அன்பு செய்வதிலும் திருச்சட்டமெல்லாம் அடங்கியிருக்கச் செய்தீரே; நாங்கள் இந்த அன்பின் கட்டளைகளைக்பகு கடைபிடித்து நிலைவாழ்விற்கு வந்து சேர அருள்புரியும். உம்மோடு…'),
('OW25-0Sun-5V', '=OW25-0Sun-1L'),
('OW26-0Sun-0V', '=OW26-0Sun-1L'),
('OW26-0Sun-1L', 'இறைவா, மன்னிப்பு அளிப்பதிலும் இரக்கம் காட்டுவதிலும் எல்லாம் வல்லவரெனச் சிறந்த முறையில் காட்டுகின்றீர், நீர் எங்களுக்கு வாக்களித்த விண்ணக மகிழ்ச்சியில், நாங்கள் பங்கு பெற முனையும் பொருட்டு உமது அருளால் எங்களைத் தொடர்ந்து நிரப்புவீராக. உம்மோடு…'),
('OW26-0Sun-5V', '=OW26-0Sun-1L'),
('OW27-0Sun-0V', '=OW27-0Sun-1L'),
('OW27-0Sun-1L', 'என்றும் வாழும் எல்லாம் வல்ல கடவுளே! எங்கள் தகுதிக்கும் வேண்டுதலுக்கும் மேலாக விளங்குகின்ற உம்  அன்பு, எங்களுக்காக உமது இரக்கத்தின் கருவூலங்களைத் சிறந்து வைக்கிறது. எங்கள் மனச்சாட்சியை அழுத்தும் பாவம் அனைத்தையும் மன்னித்து, நாங்கள் கேட்கும் மன்றாட்டுகளுக்குச் செவிசாய்த்தருள்வீராக. உம்மோடு…'),
('OW27-0Sun-5V', '=OW27-0Sun-1L'),
('OW28-0Sun-0V', '=OW28-0Sun-1L'),
('OW28-0Sun-1L', 'மாட்சி மிகுந்த ஆண்டவராகிய கடவுளே! உம் அருளுக்கு ஈடாக எம் உள்ளங்களைத் திறந்தருளும். நாங்கள் நற்செயல் புரிவதில் என்றும் கருத்தாய் இருக்க, உமது அருள் எங்களுக்கு முன்செல்லவும் எங்களோடு இருக்கவும் அருள்வீராக. உம்மோடு…'),
('OW28-0Sun-5V', '=OW28-0Sun-1L'),
('OW29-0Sun-0V', '=OW29-0Sun-1L'),
('OW29-0Sun-1L', 'எல்லாம் வல்ல கடவுளே! விருப்புடனும் காலந் தாழ்த்தாமலும் உமக்கு நாங்கள் கீழ்ப்படியும் பொருட்டு எங்களை உருவாக்கியருளும். எம் வாழ்நாளெல்லாம் நாங்கள் உண்மை, நேர்மை உள்ளத்துடன் உமக்கு ஊழியம் புரிய எங்களுக்குக் கற்றுத்தந்தருளும். உம்மோடு…'),
('OW29-0Sun-5V', '=OW29-0Sun-1L'),
('OW30-0Sun-0V', '=OW30-0Sun-1L'),
('OW30-0Sun-1L', 'ஆண்டவராகிய கடவுளே! எங்கள் நம்பிக்கையை ஆழப்படுத்தியருளும். எங்கள் எதிர்நோக்குதலை உறுதிப் படுத்தியருளும். எங்கள் அன்பைத் தூண்டி எழுப்பியருளும். இதன் வழியாக நீர் வாக்களித்ததை நாங்கள் அடையவும் நீர் கட்டளையிட்டதை நாங்கள் விரும்பி ஏற்கவும் செய்தருளும். உம்மோடு…'),
('OW30-0Sun-5V', '=OW30-0Sun-1L'),
('OW31-0Sun-0V', '=OW31-0Sun-1L'),
('OW31-0Sun-1L', 'வல்லமையும் இரக்கமும் மிகுந்த எல்லாம் வல்ல கடவுளே! உமதருளினால்தான் இறைமக்களாகிய நாங்கள் உம்மைப் புகழ்ந்து உமக்கேற்ற பணிபுரிகின்றோம். வழிதவறிச் செல்லும் எங்களைக் காப்பாற்றி, நீர் வாக்களித்த பேரின்பத்திற்கு எங்களைக் கொண்டு வந்து சேர்த்தருளும். உம்மோடு…'),
('OW31-0Sun-5V', '=OW31-0Sun-1L'),
('OW32-0Sun-0V', '=OW32-0Sun-1L'),
('OW32-0Sun-1L', 'எல்லாம் வல்ல இரக்கமுள்ள ஆண்டவரே! ஒவ்வொரு இடர்ப்பாட்டிலிருந்தும் எங்களைக் காப்பாற்றும்.   இவ்வாறு எங்கள் உள்ளத்திலும் உடலிலும் எழுகின்ற தடைகளை அகற்றி, மனமுவந்து உமக்கு நாங்கள் பணிபுரிவோமாக. உம்மோடு…'),
('OW32-0Sun-5V', '=OW32-0Sun-1L'),
('Saint Albert the Great, bishop and doctor-1L', 'ஆண்டவராகிய கடவுளே! ஆயராம் புனித பெரிய ஆல்பர்ட் இவ்வுலக ஞானத்தையும் இறை நம்பிக்கையையும் இணைப்பதில் சிறந்து விளங்கச் செய்தீர்; நாங்கள் நல்லாசிரியராகிய அவரது போதனைகளை ஏற்றுக் கொள்ளவும் ஒவ்வொரு அறிவியல் முன்னேற்றத்தின் வழியாக உம்மை ஆழ்ந்து அறிவதிலும் அன்பு செய்வதிலும் எங்களை இட்டுச் செல்லவும் அருள்புரிவீராக.'),
('Saint Anthony Mary Claret, bishop-1L', 'அருளே உருவான ஆண்டவராகிய கடவுளே! ஆயராம் புனித அந்தோணி மரிய கிளாரட் மக்கள் நடுவில் நற்செய்தியை அறிவிப்பதில் அவருக்கு வியப்புக்குரிய அன்பையும் பொறுமையையும் அளித்துத் திடப்படுத்தினீர். நாங்கள் உமது விருப்பத்தையே அனைத்திலும் தேடவும் சகோதரர்களைக் கிறிஸ்துவிடம் கொண்டு வருவதில் ஊக்கமுடன் ஈடுபடவும் அவருடைய வேண்டுதலால் எங்களுக்கு அருள் புரிவீராக.'),
('Saint Bruno, priest-1L', 'ஆண்டவராகிய கடவுளே தனிமை வாழ்வில் உமக்கு ஊழியம் புரிய புனித புருனோவை அழைத்தீர்; மாறுபட்டுக் கொண்டிருக்கும் இவ்வுலக வாழ்வின் மத்தியில் அவரது வேண்டுதலால் நாங்கள் உமது நினைவில் தொடர்ந்து நிலைத்திருக்க எங்களுக்கு அருள்வீராக.'),
('Saint Callistus I, pope and martyr-1L', 'ஆண்டவராகிய கடவுளே! உம் மக்களின் இறைவேண்டல்களுக்குக் கனிவுடன் செவிசாய்த்தருளும்; திருத்தந்தை புனித கலிஸ்துசின் மறைச்சாட்சி நிகழ்வைக் கொண்டாடி மகிழும் எங்களுக்கு, அவருடைய பேறுபலன்கள் உதவியாய் அமைவதாக.'),
('Saint Callistus I, pope and martyr-5V', 'எல்லாம் வல்ல கடவுளாகிய எங்கள் தந்தையே! புனித தெரேசாவை நிறைவான புனித வாழ்வுக்கு திருச்சபையில் சான்றாக நீர் அனுப்பினீர். அவருடைய ஆன்மீகப் போதனையால் எங்களைப் பேணிக்காத்து உண்மையான பனிதத்தை நாங்கள் விரும்பிட எங்களைத் தூண்டியருளும்.'),
('Saint Charles Borromeo, bishop-1L', 'அன்போடு எம்மை ஆதரிக்கும் ஆண்டவரே! ஆயரான புனித சார்லஸ் பொரோமியோவுக்கு நிறைவாகக் கொடுத்த ஆன்ம ஆவலை, உம் மக்களிடத்திலும் பேணிக்காத்தருள உம்மை வேண்டுகிறோம்: கிறிஸ்துவின் உருவைத் தாங்கி அவருடைய உண்மை சாயலை உலகிற்கு வெளிப்படுத்தும் வரையிலும் உமது திருச்சபையை உருவாக்கிப் புதுப்பித்தருளும்.'),
('Saint Denis and companions, martyrs-1L', 'ஆண்டவராகிய கடவுளே! உமது மாட்சியை எல்லா இனத்தவருக்கும் எடுத்துரைக்கப் புனித தியோனிசிவையும் அவருடைய தோழர்களையும் அனுப்பினீர். உம் பொருட்டு அவர்கள் உயிர் நீத்திட அவர்களைத் திடப்படுத்தினீர். நாங்கள் அவர்களது முன்மாதிரிகையில் இவ்வுலகம் தரும் இன்ப துன்பங்களைக் கருத்தில் கொள்ளாதிருக்க அருள்புரிவீராக.'),
('Saint Elizabeth of Hungary, religious-1L', 'அருள்கொடை வள்ளலாம் ஆண்டவராகிய கடவுளே! ஏழைகளில் கிறிஸ்துவைக் கண்டு பணிவிடை செய்ய ஹங்கோரி நாட்டைச் சேர்ந்த புனித எலிசபெத்துக்குக் கற்று கொடுத்தீரே, இப்புனிதரின் வேண்டுதலால் நாங்கள் ஏழைகளுக்கும் துன்புறுவோர்க்கும் எந்நாளும் அன்புத் தொண்டாற்ற அருள் புரியும்.'),
('Saint Francis of Assisi-0V', 'ஆண்டவராகிய கடவுளே! அசிசி நகர் புனித பிரான்சிஸ் ஏழ்மையிலும் தாழ்மையிலும் கிறிஸ்துவின் சாயலில் விளங்கச் செய்தீர்; இப்புனிதரின் வழியில் நடந்து மகிழ்விலும் அன்பிலும் உம் மகனைப் பின்பற்றி உம்மோடு ஒன்றித்திருக்கவும் எங்களுக்கு அருள்வீராக.'),
('Saint Francis of Assisi-1L', 'ஆண்டவராகிய கடவுளே! அசிசி நகர் புனித பிரான்சீஸ்குவை, தனது எளிமையிலும் தாழ்மையிலும் கிறிஸ்துவைபோல் விளங்கச் செய்தீர். அவரது வழிகளில் நாங்களும் நடக்கச் செய்தருளும். இதன் வழியாக நாங்கள் மகிழ்வுடனும் அன்புடனும் உம் மகனாகிய கிறிஸ்துவைப் பின்பற்றி உம்மில் ஒன்றித்திருப்போமாக.'),
('Saint Francis of Assisi-5V', '=Saint Francis of Assisi-1L'),
('Saint Gertrude the Great, virgin-1L', 'இரக்கமிக்க ஆண்டவராகிய கடவுளே! கன்னியாம் புனித ஜெர்த்ரூதின் உள்ளத்தை, உம் அன்பிற்கேற்ற ஓர் இல்லிடமாக நீர் உருவாக்கினீர். அவருடைய பரிந்துரையினால் எங்கள் உள்ளங்களில் உம் திருமுன்னிலையின் மகிழ்வை நாங்கள் அனுபவிக்கச் செய்தருளும்.'),
('Saint Gregory the Great, pope and doctor-1L', 'எங்கள் தந்தையாம் கடவுளே! உம் மக்களை அன்பினால் வழி நடத்தியும், முழு இரக்கத்தினால் பராமரித்தும் வருகின்றீர். புனித கிரகோரியாரின் பரிந்துரையின் வழியாக ஞானத்தின் ஆவியைக் கொடுத்தருளும். இவ்வாறு உமது மக்களின் ஆன்மிக வளர்ச்சி, ஆயர்களுக்கு நிலையான மகிழ்ச்சியைக் கொணர்வதாக. உம்மோடு…'),
('Saint Gregory the Great, pope and doctor-5V', '=Saint Gregory the Great, pope and doctor-1L'),
('Saint Hedwig, religious-1L', 'எல்லாம் வல்ல கடவுளே! அனைவர்க்கும் தாழ்ச்சியின் எடுத்துக்காட்டாக விளங்கிய புனித எட்விஜின் புனிதமிகு வாழ்வினால், நீர் எங்களுக்குத் துணை புரியும்படியாக உம்மை வேண்டுகிறோம்.'),
('Saint Ignatius of Antioch, bishop and martyr-1L', 'என்றும் வாழும் எல்லாம் வல்ல கடவுளே! உமது கிறிஸ்துவின் உடலாகிய திருச்சபையை மறைச்சாட்சியரின் துன்பங்கள் அணி செய்தன. புனித அந்தியோக்கு இஞ்ஞாசியாரின் துன்பங்கள், மாட்சியில் நுழைந்திட அவருக்கு உதவியதுபோல அவரது மறைச்சாட்சியத்தைக் கொண்டாடுகின்ற எங்களுக்கு, அவை நிலையான சக்தியைத் தரும் ஊற்றாக அமைந்திட அருள்வீராக.'),
('Saint Januarius, bishop and martyr-1L', 'ஆண்டவராகிய கடவுளே! உமது அருளால் புனித சனுவாரியுவின் நினைவைக் கொண்டாட எங்களுக்கு வாய்ப்பளித்துள்ளீர்: நீர் வாக்களித்த என்றுமுள்ள பேரின்ப வாழ்வை அவரோடு பகிர்ந்து கொள்ள எமக்கு அருள்புரியும்.'),
('Saint Jerome, priest and doctor-1L', 'என்றும் வாழும் எல்லாம் வல்ல கடவுளே! புனித எரோணிமு முழுஉள்ளத்தாடு விரும்பிய திருநூலின் மீதுள்ள ஆழ்ந்த ஆர்வத்தை, அவருக்குப் பரிசாகத் தந்திரே எங்களை உமது வார்த்தையால் மிகவே தாங்கி அதை வாழ்வின் ஊற்றில் கண்டடைய எமக்கு உதவுவீராக.'),
('Saint John Chrysostom, bishop and doctor-1L', 'உம்மீது நம்பிக்கை கொண்டவர்களுக்கு உறுதியளிக்கும் ஆண்டவராகிய கடவுளே! புனிதரான யோவான் கிறிசோஸ்தோம் வியத்தகு சொல்வன்மையாலும் துன்பம் தாங்கும் மனத் திடத்தா லும் திருச்சபையில் சிறந்து விளங்க வேண்டுமென் று திருவுளமானீர்; அவருடைய போதனையால் நாங்கள் வளம் பெறவும் அவரது தளராப் பொறுமையின் எடுத்துக்காட்டால் ஊக்கம் பெறவும் அருள்புரிவீராக,'),
('Saint John Leonardi, priest-1L', 'நல்லவை அனைத்தின் ஊற்றாகிய தந்தையே! புனிதலெயோனார்ட் யோவானை எல்லா இனத்தார்க்கும் நற்செய்தி அறிவிக்க நீர் அனுப்பினீர். அவருடைய பரிந்துரையால், உண்மையான நம்பிக்கையானது எங்கும் தங்கு தடையின்றி முன்னேறிச் செல்ல அருள் புரிவீராக.'),
('Saint John of Capistrano, priest-1L', 'நலமெல்லாம் நல்கிடும் எல்லாம் வல்ல கடவுளே! இடுக்கண்பட்டுழன்ற இறைமக்களுக்கு ஆறுதலளிக்க் கப்பெஸ்த்தரானோ நகர் புனித யோவானை ஏற்படுத்தினீரே; உமது பாதுகாவலினால் நாங்கள் நலமுடன் வாழவும் உமது திருச்சபை நிலையான அமைதியைப் பெறவும் உம்மை வேண்டுகிறோம்.'),
('Saint John XXIII, pope-1L', 'ஆண்டவராகிய கடவுளே! உம் மக்களின் இறைவேண்டல்களுக்குக் கனிவுடன் செவிசாய்த்தருளும்; திருத்தந்தை புனித கலிஸ்துசின் மறைச்சாட்சி நிகழ்வைக் கொண்டாடி மகிழும் எங்களுக்கு, அவருடைய பேறுபலன்கள் உதவியாய் அமைவதாக.'),
('Saint Josaphat, bishop and martyr-1L', 'ஆற்றலுக்கெல்லாம் ஊற்றாகிய ஆண்டவரே! புனித யோசபாத்து தூயஆவியாரால் நிறைந்தவராய் தம் மந்தைக்காகத் தம் உயிரைக் கொடுத்தார்; அந்தத் தூய ஆவியாரை உமது பனிதரின் வேண்டுதலின் பயனாக நாங்களும் உமதருளால் திருச்சபைமீது பொழிந்தருளும் திடமடைந்து, எங்கள் சகோதரர் சகோதரிகளுக்காக உயிர் துறக்கவும் தயங்காதிருக்கச் செய்தருளும்.'),
('Saint Leo the Great, pope and doctor-1L', 'அனைத்தையும் அமைத்தாளும் ஆண்டவராகிய கடவுளே! நீர் திருத்தூதர் பேதுருவை உறுதியான அடித்தளமாகக் கொண்டு, உமது திருச்சபையைக் கட்டி எழுப்பினீர். அதை நரகத்தின் வாயில்கள் ஒருபோதும் மேற்கொள்ளாது என்று நீர் வாக்களித்தீர், தலைமை ஆயரான புனித லெயோவின் வேண்டுதலின் உறுதுணையால் உமது திருச்சபை உமது உண்மைக்கு நம்பிக்கைக்குரியதாய் விளங்கவும் என்றுமுள்ள அமைதியில் நிலைத்து நிற்கவும் உம்மை வேண்டுகிறோம்.'),
('Saint Luke the Evangelist-1L', 'ஆண்டவராகிய கடவுளே! ஏழைகள் மீது உமக்குள்ள அன்பின் மறைபொருளைத் தமது மறையுரையாலும் எழுத்தாலும் வெளிப்படுத்த புனித லூக்காவைத் தேர்ந்தெடுத்தீர்; கிறிஸ்தவர்கள் என்னும் பெயரில் பெருமை கொள்ளும் நாங்கள், ஒரே உள்ளமும் ஒரே மனமும் கொண்டு வாழவும் அதனால் எல்லா நாட்டினரும் மீட்பைக் காணவும் செய்தருள்வீராக.'),
('Saint Luke the Evangelist-5V', '=Saint Luke the Evangelist-1L'),
('Saint Margaret Mary Alacoque, virgin-1L', 'எங்கள் தந்தையாகிய கடவுளே! புனித மார்கரீத் மரியாவிற்கு நீர் அபரிமிதமாக அளித்த அன்பின் ஆவியால எங்கள் உள்ளங்களை நிரப்பியருளும். அனைத்து அறிவுக்கும் மேம்பட்ட கிறிஸ்துவின் அன்பைத் துய்த்துணர்ந்து. நீர் எமக்குக் குறித்து வைத்திருக்கும் நிறைவாழ்வை அடைய அருள்வீராக.'),
('Saint Margaret of Scotland-1L', 'எல்லாம் வல்ல கடவுளே! ஸ்காட்லாந்து அரசியான புனித மார்கரீத், ஏழைகள் பால் கொண்ட மிகச்சிறந்த அன்பினால் அவரை வியப்புக்குரியவராய்த் திகழச் செய்தீர். அவருடைய எடுத்துக்காட்டைப் பின்பற்றி அவரது இறைவேண்டுதலின் துணையாலும் எங்கள் வாழ்வினால் உமது நன்மைத்தனம், அன்பு ஆகியவற்றுக்கு மனிதர்முன் நாங்கள் சான்று பகர்வோமாக.'),
('Saint Martin de Porres, religious-1L', 'பரிவன்புமிக்க ஆண்டவராகிய கடவுளே! புனித மார்ட்டின் தெபோரஸைத் தாழ்ச்சியின் பாதையில் நடத்திச் சென்று, விண்ணக மாட்சிக்கு உயர்த்தினீரே நாங்கள் இன்று அவருடைய புகழ்வாய்ந்த முன்மாதிரியைப் பின்பற்றி, அவரைப்போல உமது ஆட்சியில் ஓரிடத்தை உம்மிடமிருந்து பெற்றுக்கொள்வோமாக.'),
('Saint Martin of Tours, bishop-1L', 'எல்லாம் வல்ல ஆண்டவராகிய கடவுளே! ஆயரான வத மார்ட்டினுடைய வாழ்வினாலும் இறப்பினாலும் மாட்சி அடைந்தீரே; எங்கள் உள்ளங்களில் உமது அருளின் வியத்தகு செயல்களைப் புதுப்பித்து, நாங்கள் வாழ்விலும் இறப்பிலும் உமது இறை அன்பிலிருந்து பிரிந்திருக்க விடாதேயும்.'),
('Saint Martin of Tours, bishop-5V', '=Saint Martin of Tours, bishop-1L'),
('Saint Matthew the Evangelist, Apostle, Evangelist-1L', 'எம் தலைவராகிய ஆண்டவரே வரி தண்டுபவராகிய மத்தேயுவைச் சொல்லற்கரிய உமது இரக்கத்தால் திருத்தூதராக விளங்கும்படி அழைத்தீர் அவரது பரிந்துரையாலும் முன்மாதிரிகையாலும் நாங்கள் எப்போதும் உமது அழைத்தலுக்குப் பதிலுரைக்கவும் உம்மோடு நெருங்கிய தொடர்பு கொண்டு வாழவும் துணைபுரிவீராக.'),
('Saint Paul of the Cross, priest-1L', 'எல்லாம் வல்ல கடவுளே! சிலுவைமீது மட்டுமே அன்பு கொண்டிருந்த உமது மறைப்பணியாளராம் புனித பவுல், தம் வேண்டுதலால் உமதருளை எங்களுக்குப் பெற்றுத் தருவாராக. அவருடைய முன்மாதிரியின் நாங்கள் தூண்டுதலைப் பெற்று, மனவுறுதியுடன் எங்கள் சிலுவையை அரவணைத்துக் கொள்ளச் செய்தருளும்.'),
('Saint Robert Bellarmine, bishop and doctor-1L', 'ஆண்டவராகிய கடவுளே! உமது திருச்சபையின் நம்பிக்கையைப் பாதுகாக்க, ஆயரும் மறைநூல் வல்லுநருமான புனித இராபர்ட் பெல்லார்மின்மேல் வியத்தகு அறிவாற்றலையும் தூய்மையையும் பொழிந்தீர் உம்முடைய மக்கள் நாங்கள் என்றும் அந்த நம்பிக்கையின் நிறைவைப் பெற்று மகிழ, அவருடைய பரிந்துரையால் எங்களுக்கு அருள்வீராக'),
('Saint Simon and Saint Jude, apostles-1L', 'ஆண்டவராகிய கடவுளே! புனித திருத்தூதர்களின் போதனை வழியாக, நாங்கள் உம்மை அறிந்து ஏற்றுக் கொள்ளச் செய்தீர்; சீமோன், யூதா என்னும் புனிதர்களின் வேண்டுதலால் உம்மில் நம்பிக்கை கொள்ளும் மக்கள் பெருகி, திருச்சபை என்றும் வளர்ச்சி பெறச் செய்தருளும்.'),
('Saint Teresa of Jesus, virgin and doctor-5V', 'எல்லாம் வல்ல கடவுளாகிய எங்கள் தந்தையே! புனித தெரேசாவை நிறைவான புனித வாழ்வுக்கு திருச்சபையில் சான்றாக நீர் அனுப்பினீர். அவருடைய ஆன்மீகப் போதனையால் எங்களைப் பேணிக்காத்து உண்மையான புனிதத்தை நாங்கள் விரும்பிட எங்களைத் தூண்டியருளும்.'),
('Saint Thérèse of the Child Jesus, virgin and doctor-1L', 'எங்கள் தந்தையாம் கடவுளே சிறுவர்க்கும் உள்ளத்தில் பணிவு கொண்டேருக்கும் உமது ஆட்சியை நீர் வாக்களித்தீர். குழந்தை இயேசுவின் புனித தெரேசாவின் சிறிய வழியில் நாங்கள் நம்பிக்கையுடன் நடக்க எங்களுக்கு அருள் தாரும். அவருடைய வேண்டுதலின் உதவியினால் நாங்கள் நிலை மாட்சியைக் காண்போமாக.'),
('Saint Thérèse of the Child Jesus, virgin and doctor-5V', '=Saint Thérèse of the Child Jesus, virgin and doctor-1L'),
('Saint Vincent de Paul, priest-1L', 'தந்தையாம் கடவுளே! ஏழைகளுக்கு மீட்பு அளிக்கவும் மறைப்பணியாளர்களின் வாழ்வை முறைப்படுத்தவும் மறைப்பணியாளராகிய புனித வின்சென்ட்டுக்குத் திருத்தூதரின் உள்ளார்வத்தை நிறைவாக அளித்தீர் நாங்களும் அவரது மனப்பான்மையில் பங்கேற்று அவர் அன்பு செய்ததை அன்பு செய்யவும் அவர் போதித்ததைச் செயல்படுத்தவும் எங்களுக்கு அருள்வீராக.'),
('Saint Wenceslaus, martyr-1L', 'என்றும் வாழும் எல்லாம் வல்ல கடவுளே! விண்ணக அரசின் மாட்சியின் பொருட்டு மண்ணக அரசை புறம்பே தள்ளிட புனித வென்செஸ்லாஸ் உம் வழிநடத்துதலின் வழியாக கற்றுக் கொண்டார் அவரது இறைவேண்டுதலால் நாங்கள் தன்னலம் துறந்து, முழு இதயத்தோடு உம்மைப் பின்தொடர எமக்கு அருள்வீராக.'),
('Saints Cornelius, pope, and Cyprian, bishop, martyrs-1L', 'ஆண்டவராகிய கடவுளே! கொர்னேலியு, சிப்பிரியான் என்னும் புனிதர்களை உண்மையுள்ள மறைப்பணியாளர்களாகவும் நிலையுறுதியை உடைய மறைசாட்சியராகவும் உம் திருச்சபைக்கு நீர் வழங்கினீர்; அர்களுடைய வேண்டுதலால், நாங்கள் நம்பிக்கையில் உறுதியுடன் நிலைத்து நிற்கவும் திருச்சபையின் ஒருமைப்பாட்டிற்காக எல்லாவிதமான எங்கள் முழு ஆற்றலுடன் உழைக்கவும் எங்களுக்கு அருள்வீராக.'),
('Saints Cosmas and Damian, martyrs-1L', 'பரிவன்புமிக்க ஆண்டவராம் கடவுளே புனித கோஸ்மா, தமியான் என்னும் மறைச்சாட்சியரின் நினைவுநாளை நாங்கள் கொண்டாடுவதன் வழியாக உமது பேராற்றலைப் பறைசாற்றுகின்றோம் உமது பராமரிப்பினால் அவர்களை மாட்சியில் உயர்த்தினீர் அவர்களது பாதுகாப்பு மிக்க இறைவேண்டல் எங்களுக்குக் கிடைக்க அருள் புரியும்.'),
('Saints Jean de Brébeuf, Isaac Jogues, priests and martyrs; and their companions, martyrs-1L', 'பாரினைப் பராமரிக்கும் ஆண்டவராகிய கடவுளே! புனிதர்களான ஈசாக்கும் அவருடைய தோழர்களும் சிந்திய இரத்தத்தாலும் போதித்த போதனையாலும் வட அமெரிக்கப் பகுதிகளில் திருமறையின் முதற்கனிகளைப் புனிதப்படுத்தினீர். அவர்களுடைய மன்றாட்டினால் கிறிஸ்தவர்களின் திருச்சபை ஒவ்வொரு நாட்டிலும் நிறைவளர்ச்சி காண்பதாக.'),
('Saints Michael, Gabriel and Raphael, Archangels-1L', 'படைகளின் ஆண்டவராகிய வானதூதருக்கும் மானிடருக்கும் அவரவருக்குரிய பணிகளை ஞானமிகு முறையில் திட்டமிட்டருளினீர்; விண்ணகத்தில் உம்மை வழிபடும் தூதர்கள் மண்ணகத்தில் எங்கள் வாழ்வைப் பாதுகாத்திட அருள் புரிவீராக.'),
('Saints Michael, Gabriel and Raphael, Archangels-5V', '=Saints Michael, Gabriel and Raphael, Archangels-1L'),
('W0-0Sun-0C', 'ஆண்டவரே, இவ்விரவில் எங்களைச் சந்திக்க வாரும்; இதனால் உம் மகனாம் கிறிஸ்துவின் உயிர்ப்பில் அக்களித்து உமது ஆற்றலால் வைகறையில் நாங்கள் துயிலெழுவோமாக. உம்மோடு என்றென்றும் வாழ்ந்து ஆட்சி செய்கின்ற அவர் வழியாக உம்மை மன்றாடுகிறோம்.'),
('W0-0Sun-6C', 'எங்கள் தந்தையாகிய கடவுளே, நாங்கள் இன்று ஆண்டவருடைய உயிர்ப்பின் மறைபொருளைக் கொண்டாடுவதால், நாங்கள் எல்லாத் தீமைகளினின்றும் விடுதலையடைந்து, அமைதியில் கண்ணுறங்கவும் உம் புகழ்ச்சியைப் பாட மகிழ்ச்சியோடு விழித்துக் கொள்ளவும் வேண்டுமென்ற எங்கள் பணிவான வேண்டுதலைக் கேட்டருளும். எங்கள் ஆண்டவராகிய கிறிஸ்து வழியாக உம்மை மன்றாடுகிறோம்.'),
('W0-0Sun-6C-S', 'ஆண்டவரே, எம் இல்லத்தைச் சந்தித்துப் பகைவனின் கண்ணிகள் அனைத்தையும் எங்கள் இல்லத்திலிருந்து அகற்றியருள உம்மை மன்றாடுகிறோம்; உம்முடைய தூதர்கள் இந்த இல்லத்தில் குடியிருந்து எங்களை அமைதியில் காப்பார்களாக. உமது ஆசி எங்கள் மீது என்றென்றும் தங்குவதாக. எங்கள் ஆண்டவராகிய கிறிஸ்து வழியாக உம்மை மன்றாடுகிறோம்.'),
('W0-1Mon-3S', 'திராட்சைத் தோட்டத்தின் ஆண்டவரும் தலைவருமான கடவுளே, நாங்கள் செய்யவேண்டிய அலுவலையும், அதற்குரிய நியாயமான ஊதியத்தையும் கொடுக்கின்றவர் நீர். எங்களது அன்றாட வாழ்வின் சுமையை நாங்கள் தாங்கிக் கொண்டு முறைப்பாடின்றி அனைத்திலும் உமது திருவுளத்தை ஏற்றுக் கொள்ள எங்களுக்கு உதவியருளும். எங்கள் ஆண்டவர் கிறிஸ்து வழியாக உம்மை மன்றாடுகிறோம்.'),
('W0-1Mon-6C', 'ஆண்டவரே, நலமளிக்கும் இளைளப்பாற்றியை எங்கள் உடலுக்குத் தந்தருளும்; எங்களுடைய இன்றைய உழைப்பு நிலைவாழ்வில் என்றுமுள்ள மாட்சிக்கு விதையாக அமைவதாக, எங்கள் ஆண்டவர் கிறிஸ்து வழியாக உம்மை மன்றாடுகிறோம்.'),
('W0-2Tue-3S', 'எல்லாம் வல்ல கடவுளே, அனைத்து மாந்தரின் மீட்பைப் பற்றிய உமது திட்டத்தை நீர் பேதுருவுக்கு வெளிப்படுத்தினீர்; எங்கள் பணிகள் உமக்கு ஏற்றனவாய் இருக்கவும் உமது அருளால் அப்பணி உமது மீட்பின் உயர்ந்த நோக்கத்தை நிறைவேற்றவும் செய்தருளும். எங்கள் ஆண்டவர் கிறிஸ்து வழியாக உம்மை மன்றாடுகிறோம்.'),
('W0-2Tue-6C', 'ஆண்டவரே, உமது இரக்கத்தால் இவ்விரவின் இருளை விரட்டியருளும். உமது பணியாளர்கள் அமைதியில் உறங்கவும் உமது திருப்பெயரில் மகிழ்வுடன் விழித்தெழுந்து புதிய நாளின் ஒளியைக் காணவும் கருணை கூர்ந்தருளும். எங்கள் ஆண்டவராகிய கிறிஸ்து வழியாக உம்மை மன்றாடுகிறோம்.'),
('W0-3Wed-3S', 'ஆற்றலும் அன்பும் மிக்க கடவுளே, நாங்கள் தொடங்கியுள்ள பணியைக் கனிவுடன் கண்ணோக்கி இந்த நண்பகல் வேளையில் எமக்குள் உமது அருளைப் புதுப்பித்தருளும்; எங்கள் பணியின் குறைகளை நீக்கி, அதை உமது திருவுளத்திற்கு ஏற்றபடி நிறைவடையச் செய்தருளும், உம்மோடு தூய ஆவியாரின் ஒன்றிப்பில், கடவுளாய் என்றென்றும் வாழ்ந்து ஆட்சி செய்கின்ற எங்கள் ஆண்டவரும் உம் மகனுமாகிய இயேசு கிறிஸ்து வழியாக உம்மை மன்றாடுகிறோம்.'),
('W0-3Wed-6C', 'ஆண்டவராகிய இயேசு கிறிஸ்துவே, இதயத்தில் கனிவும் தாழ்மையும் உள்ளவரே, உம்மைப் பின்பற்றுகிறவர்களுக்கு இனிய நுகத்தையும் இதமான சுமையையும் கொடுக்கிறீர். இந்நாளில் எங்கள் வேண்டல்களையும் உழைப்பினையும் ஏற்றுக்கொண்டு எந்நாளும் நாங்கள் விருப்புடன் உமக்கு ஊழியம் புரியத் தேவையான இளைப்பாற்றியை எமக்கருள உம்மைக் கெஞ்சிக் கேட்கிறோம். என்றென்றும் வாழ்ந்து ஆட்சி செய்கின்றவரே, உம்மை மன்றாடுகிறோம்.'),
('W0-4Thu-3S', 'எல்லாம் வல்ல கடவுளே, நீரே ஒளி; இருள் என்பது உம்மிடம் இல்லை: உமது பேரொளி எங்கள்மேல் ஒளிர்வதாக. இதனால், உமது கட்டளைகள் காட்டும் வழியில் நாங்கள் மகிழ்வுடன் நடப்போம். எங்கள் ஆண்டவராகிய கிறிஸ்துவின் வழியாக உம்மை மன்றாடுகிறோம்.'),
('W0-4Thu-6C', 'எங்கள் ஆண்டவராகிய கடவுளே! இந்நாளில் உழைப்பால் நாங்கள் மிகவே சோர்வடைந்துள்ளோம்; அமைதியான உறக்கத்தால் எங்களைப் புதுப்பித்தருளும். இவ்வாறு, உமது உதவியால் சோர்வு நீங்கிய நாங்கள் உடலாலும் உள்ளத்தாலும் உமக்குப் பணிபுரிவோமா. எங்கள் ஆண்டவராகிய கிறிஸ்து வழியாக உம்மை மன்றாடுகிறோம்.'),
('W0-5Fri-3S', 'ஆண்டவராகிய இயேசு கிறிஸ்துவே, உலகம் முழுவதும் இருளில் மூழ்கியிருந்த நேரத்தில் நீர் எங்கள் மீட்புக்காக மாசற்ற பலிப்பொருளாகத் திருச்சிலுவையில் உயர்த்தப்பட்டீர்; நிலைவாழ்வை இந்த மணி நேரத்தில் நாங்கள் அடைவதற்குத் தேவையான ஒளியை எங்களுக்கு அருள்வீராக. என்றென்றும் வாழ்ந்து ஆட்சி செய்கின்றவர் நீரே.'),
('W0-5Fri-6C', 'எல்லாம் வல்ல கடவுளே! இறந்து, கல்லறையில் புதைக்கப்பட்ட உம் ஒரே மகனோடு நாங்கள் நம்பிக்கையினால் ஒன்றிணைய எங்களுக்கு உமதருளைத் தந்தருளும். இதனால் நாங்கள் அவரோடு புதிய வாழ்வுக்கு உயிர்த்தெழுவோமாக. எங்கள் ஆண்டவராகிய கிறிஸ்து வழியாக உம்மை மன்றாடுகிறோம்.'),
('W0-6Sat-3S', 'ஆண்டவராகிய கடவுளே, நிலையன்பின் உயிருள்ள ஒளியே, நாங்கள் அன்பின் ஆர்வத்தால் தூண்டப்பட்டு எப்பொழுதும் அனைத்திற்கு மேலாக உம்மையும் உம் பொருட்டு எம் சகோதரர் சகோதரிகளையும் அன்பு செய்ய வரமருள்வீராக. எங்கள் ஆண்டவர் கிறிஸ்து வழியாக உம்மை மன்றாடுகிறோம்.'),
('W1-1Mon-1L', 'ஆண்டவரே, எங்கள் சொல், செயல் அனைத்திற்கும் நீரே தொடக்கமும் முடிவுமாக இருப்பீராக. உமது அருளால் எமது செயல்களைத் தூண்டி, உமது ஆற்றல்மிக்க துணையால் அவற்றை முழுமை பெறச்செய்வீராக. உம்மோடு…'),
('W1-1Mon-5V', 'ஆண்டவரே. எமது மீட்பை முன்னிட்டு உம் அடியாராகிய மரியாவின் தாழ்நிலையைக் கண்ணோக்கினீர்; எமது வழிபாடு உமக்கு மாட்சி அளிப்பதாக. மீட்பின் நிறைவை, மரியாவோடு பகிர்ந்து கொள்ள எங்களை விழித்தெழச் செய்யும். உம்மோடு…'),
('W1-2Tue-1L', 'ஆண்டவரே, கருணையோடு எமது காலை வேண்டலைக் ஏற்றருளும். உமது மீட்கும் அன்பினால், எம் உள்ளத்தின் அடித்தளத்தில் உமது அருள்ஒளி ஊடுருவச் செய்தருளும். இதனால் தகாத ஆசைகளால் எங்கள் உள்ளம் இருளடையாதிருப்பதாக; உமது விண்ணக அருளினால் புத்தொளி பெற்று நாங்கள் புதுப்பிக்கப்படுவோமாக. உம்மோடு…'),
('W1-2Tue-5V', 'ஆண்டவரே, எல்லாம் வல்ல கடவுளே! நீர் எங்களை இன்றைய நாளின் இறுதிப் பொழுது வரையிலும் நலமுடன் பாதுகாத்தமைக்கு உமக்கு நன்றி கூறுகிறோம்; வான்நோக்கிக் கரங்கூப்பி நாங்கள் எழுப்பும் எங்கள் வேண்டுதல் உமக்குகந்த காணிக்கையாக வேண்டுமென்று நாங்கள் தாழ்மையுடன் உம்மை மன்றாடுகிறோம். உம்மோடு…'),
('W1-3Wed-1L', 'எம் மீட்பராகிய கடவுளே! திருமுழுக்கு அருளால் நீர் எம்மை ஒளியின் மக்களாக்கினீர்; நாங்கள் அந்த ஒளியிலேயே எப்பொழுதும் நடக்கவும் உண்மைக்காக உழைக்கவும் துணைபுரிந்தருளும்; இதனால் மக்கள் அனைவருக்கும் முன்பாக உமக்குச் சான்றுகளாய் திகழ்வோமாக. உம்மோடு…'),
('W1-3Wed-5V', 'ஆண்டவரே, எங்கள் மன்றாட்டைக் கேட்டு எம்மை இரவும் பகலும் பாதுகாத்தருளும்; இதன் வழியாக உமது வழிநடத்தும் கரத்தினால், மாறும் உலகில் வாழ்கின்ற நாங்கள், மாறாப் பாறையாகிய உம்மிடம் இருந்து ஆற்றலை எப்போதும் பெற்றுக் கொள்வோமாக. உம்மோடு…'),
('W1-4Thu-1L', 'என்றும் வாழும் எல்லாம் வல்ல கடவுளே! எங்கள் உள்ளங்களினின்று பாவ இருளைப் போக்கவும், அவற்றை உண்மை ஒளியாகிய உம் மகன் கிறிஸ்துவிடம் எங்களைக் கொண்டுவந்து சேர்க்கவும் காலை, நண்பகல், மாலை வேளைகளில் உம்மை வேண்டுகிறோம். என்றென்றும் வாழ்ந்து ஆட்சி செய்கின்றவர் நீரே.'),
('W1-4Thu-5V', 'கடவுளாகிய ஆண்டவரே, நிலவின் ஒளியைக் கொண்டு இருளை விரட்டுகின்றீர்; பகலை ஒளிமயமாகச் செய்கின்றீர். இன்று இரவில் நாங்கள் அலகையின் பிடியினின்று விடுதலை பெறவும் அதிகாலையில் உம்மைப் புகழ்ந்தேத்த விழித்தெழவும் செய்வீராக. உம்மோடு…'),
('W1-5Fri-1L', 'ஆண்டவராகிய கடவுளே, உம்மை அறியாத மக்களுக்கு உமது வார்த்தையாம் ஒளியை வெளிப்படுத்தினீர்; நீர் எங்களுக்குத் தந்த நம்பிக்கையை எங்கள் உள்ளங்களில் திடப்படுத்தியருளும். இதன் வழியாக, உமது ஆவியானவர் எங்களில் மூட்டியுள்ள நம்பிக்கையெனும் தீயை, சோதனைகள் ஒருபோதும் அணைக்காதிருப்பனவாக. உம்மோடு…'),
('W1-5Fri-5V', 'ஆண்டவராகிய கடவுளே! உம் மகனின் பாடுகளின் படிப்பினைகளை எங்களுக்குக் கற்றுத்தாரும். மேலும் எங்களுக்காக எளிதாக்கிய சுமையைச் சுமக்க, உம் மக்களாகிய எங்களுக்கு ஆற்றலைத் தந்தருளும். உம்மோடு…'),
('W1-6Sat-1L', 'ஆண்டவராகிய இயேசுவே, உமது உயிர்ப்பின் பேரொளி எங்கள் உள்ளத்தையும் மனதையும் ஒளிர்விப்பதாக; இதனால் சாவின் இருளினின்று தப்பிக்கச்செய்து, நிலைவாழ்வின் பேரொளிக்கு எங்களைக் கொண்டு வருவீராக. உம்மோடு3…'),
('W2-1Mon-1L', 'எல்லாம் வல்ல ஆண்டவராகிய கடவுளே! நாளின் தொடக்கத்திற்கு எங்களை நீர் கொண்டுவந்திருக்கிறீர். இந்நாள் முழுவதும் நாங்கள் பாவத்திற்கு இடம் கொடாதபடி உமது வல்லமையால் எங்களைப் பாதுகாத்தருளும். இதனால் எங்கள் சிந்தனை. சொல், செயல் அனைத்திலும் உமக்கு விருப்பமானவற்றையே செய்வோமாக. உம்மோடு…'),
('W2-1Mon-5V', 'எல்லாம் வல்ல கடவுளே! தகுதியற்ற ஊழியர்களாகிய எங்களுக்கு, இந்நாள் முழுவதும் உழைப்பதற்கான வலிமையைத் தந்துள்ளீர். உமது கொடைகளுக்கு நன்றியாக இம்மாலை வேளையில் நாங்கள் ஒப்புக்கொடுக்கும் புகழ்ச்சிப் பலியை ஏற்றருளும். உம்மோடு…'),
('W2-2Tue-1L', 'ஆண்டவராகிய இயேசு கிறிஸ்துவே, உலகின் உண்மையான ஒளியே, அனைத்து மாந்தர்மீதும் உமது மீட்பன் ஒளியை ஒளிரச் செய்கின்றீர், இவ்வுலகில் நீதியையும் அமைதியையும் நிலைநாட்டுவதன் வழியாக உமது வருகையை நாங்கள் பறைசாற்றிட எங்களுக்கு அருள் தாரும். உம்மோடு3…'),
('W2-2Tue-5V', 'ஆண்டவராகிய கடவுளே! பகலும் இரவும் உமக்கே சொந்தம். எப்பொழுதும் எம் உள்ளங்களில் நீதியின் கதிரோனை ஒளிரச் செய்தருளும்; இதனால் உமது என்றுமுள்ள இல்லிடமாகிய அந்தப் பேரொளியை நாங்கள் வந்தடைவோமாக. உம்மோடு…'),
('W2-3Wed-1L', 'ஆண்டவரே, உமது தூய ஒளியை எமது இதயங்களில் பொழிந்தருளும், நாங்கள் உமது கட்டளைகளின் பாதையில் இடைவிடாது நடப்பதால் எந்நாளும் நாங்கள் ஏமாற்றப்பட்டுத் தவறில் விழாதிருக்கச் செய்தருளும். உம்மோடு…'),
('W2-3Wed-5V', 'ஆண்டவராகிய கடவுளே, தூயவர் என்பதே உமது பெயர்; உமது இரக்கம் ஒவ்வொரு தலைமுறையிலும் பறைசாற்றப்படுகின்றது. உம் மக்களின் இறைவேண்டலை ஏற்றருளும். இடைவிடாத புகழ்ச்சியால் உமது பெருமையை அவர்கள் பாடச் செய்தருளும். உம்மோடு…'),
('W2-4Thu-1L', 'ஆண்டவராகிய கடவுளே, உண்மை ஒளியும், ஒளியைப் படைத்தவரும் நீரே. நாங்கள் எப்பொழுதும் தூய்மையான உண்மையாக சிந்திப்பதன் வழியாக உமது திருமுன்னிலையின் பேரொளியில் நாங்கள் என்றும் வாழ்வோமாக. தூய எண்ணங்களால் நிறைந்து, உமது பேரொளியின் திருமுன்னிலையில் வாழ அருள்கூரும். உம்மோடு…'),
('W2-4Thu-5V', 'ஆண்டவரே, நாங்கள் உமது கருணையைக் கெஞ்சிக் கேட்கிறோம். இந்த மாலைப் புகழை நாங்கள் உமக்கே அர்ப்பணிக்கின்றோம். எமது உள்ளம் எப்பொழுதும் உமது திருச்சட்டத்தைத் தியானிக்குமாறு அருள்புரியும். எங்களுக்கு நிலைவாழ்வின் ஒளியையும் பரிசையும் அளித்தருளும். உம்மோடு…'),
('W2-5Fri-1L', 'எல்லாம் வல்ல கடவுளே! இன்று இந்தக்காலை இறைவேண்டலில், நாங்கள் எங்கள் புகழ்ச்சியை உமக்கு செலுத்துவதன் வழியாக, உம்முடைய புனிதர்களோடு நாங்கள் பெருமகிழ்வுடன் உம்மைப் புகழ அருள் புரியும். உம்மோடு…'),
('W2-5Fri-5V', 'ஆண்டவராகிய கடவுளே! நம்பிக்கையற்றவர்களுக்கு இடறலாகவும், நம்பிக்கைக் கொண்ட எங்களுக்கு உமது வல்லமை, ஞானம் இவற்றின் அடையாளமாகவும் உள்ள உமது அன்பின் மறைபொருளைச் சிலுவை வெளிப்படுத்துகிறது. உம் மகனுடைய மாட்சிமிக்க பாடுகளை நன்கு தியானிக்க, எங்களுக்குக் கற்றுத்தாரும்; இதனால் அவருடைய திருச்சிலுவையில் நாங்கள் எப்பொழுதும் நம்பிக்கைக் கொண்டு மாட்சியடைவோமாக. உம்மோடு2…'),
('W2-6Sat-1L', 'ஆண்டவரே, நாங்கள் எமது குரலாலும் மனத்தாலும் செயலாலும் உம்மைப் புகழ்வோமாக. எமது வாழ்வே உமது கொடையாகையால், நாங்கள் கொண்டிருப்பவைகளும் நாங்களும் உமது உடைமைகள் ஆவோமாக. உம்மோடு…'),
('W3-1Mon-1L', 'ஆண்டவராகிய கடவுளே! நீரே விண்ணிற்கும் மண்ணிற்கும் அரசர்: இன்று எங்கள் உள்ளத்தையும் உடலையும் ஆண்டு நடத்தியருளும், உமது திருச்சட்டத்தின் கட்டளைகளுக்கேற்ப எங்களைப் புனிதப்படுத்தி, எங்கள் சிந்தனை, சொல், செயல் இவற்றை நெறிப்படுத்தியருளும். இவ்வாறு உமது அருளினால் இன்றும் என்றும் விடுதலையடைந்து மீட்கப்படுவோமாக, உம்மோடு…'),
('W3-1Mon-5V', 'ஆண்டவராகிய கடவுளே! எவ்வித மாறுபாடோ மாற்றமோ இல்லாத உம்மைப் பேரொளி எனப் பறைசாற்றுவது எங்கள்மீது சுமத்தப்பட்ட கடமையாகும் இந்நாளின் மாலை வேளையை அடைந்திருக்கிறோம்; எம் அக இருளை அகற்றி, எம் பாவங்களை இரக்கமுடன் மன்னித்தருளும். உம்மோடு…'),
('W3-2Tue-1L', 'எல்லாம் வல்ல கடவுளே! அனைத்து நலன்களும் அழகும் கொண்ட இவ்வுலகம் உமக்கே சொந்தம். இந்நாளை உமது திருப்பெயரில் மகிழ்வுடன் தொடங்கவும் எங்கள் இறையன்பு, பிறரன்புப் பணிகளால் நிறைவு செய்யவும் அருள்புரியும். உம்மோடு…'),
('W3-2Tue-5V', 'ஆண்டவரே, எங்கள் மாலைப்புகழ் உமது இரக்கத்தின் அரியணையின்முன் எழுவதாக. உமது அருளாசியை எம்மீது பொழிந்தருளும். இதன் வழியாக இன்றும் என்றும் உமது அருள் எமக்குத் துணையாயிருந்து எமக்கு மீட்பளிப்பதாக. உம்மோடு…'),
('W3-3Wed-1L', 'ஆண்டவராகிய கடவுளே! உமது ஞானத்தில் எங்களைப் படைத்து, உம் பராமரிப்பால் எங்களை ஆண்டு நடத்துகிறீர். உமது தூய ஒளியால் எங்கள் இதயத்தின் உள் ஆழத்தை ஊடுருவியருளும்; அதனால் எங்கள் வாழ்வு எப்போதும் உமக்கு உண்மையுள்ள பணியாக அமைவதாக. உம்மோடு…'),
('W3-3Wed-5V', 'ஆண்டவரே, உமது மக்கள் உம் திருமுன் எழுப்பும் கூக்குரல் கேட்டு, நீர் அவர்களுடைய பாவங்களை மன்னித்தருளும். இவ்வாறு அவர்கள் உமது அருளால் உம் பணிக்கு அர்ப்பணிக்கப்பட்டவர்கள் ஆவார்களாக. அவர்களை உமது அருள்கரத்தால் பாதுகாத்தருளும். உம்மோடு…'),
('W3-4Thu-1L', 'என்றும் வாழும் எல்லாம் வல்ல கடவுளே! வானிலிருந்து நீதியின் கதிரவனாகிய எங்கள் ஆண்டவராம் இயேசு கிறிஸ்து எங்கள் நடுவில் வந்தபொழுது, நீர் உம் மாட்சியின் ஒளியைப் பொழிந்தீர்; அதைப்போன்று சாவின் நிழலில் வாழும் அனைவர்மீதும் இன்றும் உமது மாட்சியின் சுடரொளியை வீசச் செய்தருளும். உம்மோடு…'),
('W3-4Thu-5V', 'ஆண்டவரே, இந்நாள் முடிவில் நாங்கள் எங்கள் நன்றிக் காணிக்கையைக் கொண்டு வருகிறோம். எங்கள் மனித வலுக்குறைவினால் நாங்கள் கட்டிக்கொண்ட பாவங்களையெல்லாம் உம் இரக்கத்தால் மன்னித்தருளும்படி உம்மைக் கெஞ்சி மன்றாடுகிறோம். உம்மோடு…'),
('W3-5Fri-1L', 'எல்லாம் வல்ல தந்தையே, எங்கள் உள்ளங்களில் உமது ஒளியை ஊடுருவச் செய்தருளும்; அதனால் உம்முடைய கட்டளைகளின் வழியில் நாங்கள் நடந்து எங்கள் தலைவரும் வழிகாட்டியுமாகிய உம்மை என்றும் பின்பற்றுவோமாக. உம்மோடு…'),
('W3-5Fri-5V', 'தூய தந்தையாகிய ஆண்டவரே! உம் மகன் இயேசு கிறிஸ்துவே எங்கள் மீட்புக்கு விலையாக இருக்க வேண்டுமென்று நீர் திருவுளமானீர்; நாங்கள் அவருடைய பாடுகளில் பங்கேற்று, அவரது உயிர்ப்பின் ஆற்றலினால் உறுதி பெற்று வாழ எங்களுக்கு அருள் புரிவீராக. உம்மோடு2…'),
('W3-6Sat-1L', 'ஆண்டவராகிய கடவுளே! நீரே எமது மீட்புக்குத் தொடக்கமும் ஊற்றுமாயிருக்கின்றீர்: எமது இவ்வுலக வாழ்வினால் உமது மாட்சியை வெளிப்படுத்தவும் விண்ணகத்தில் முடிவின்றி நாங்கள் உம்மைப் போற்றிப் புகழவும் செய்தருள்வீராக. உம்மோடு…'),
('W4-1Mon-1L', 'கடவுளாகிய ஆண்டவரே, பூமியை கைக்கொண்டு அதனைப் பயிரிடுமாறு மனிதரிடம் அதனை ஒப்படைத்தீர்; அவர்களுடைய நன்மைக்காகக் கதிரவனையும் ஒளிரச் செய்தீர். உமது மாட்சிக்காகவும் எங்களை அடுத்திருப்போரின் நலன்களுக்காகவும் நாங்கள் நம்பிக்கையுடன் பணிபுரிய எங்களுக்கு இன்று உமதருளைத் தந்தருளும். உம்மோடு…'),
('W4-1Mon-5V', 'ஆண்டவராகிய இயேசுவே, மாலை நேரமாகிறது; எம்மோடு தங்கும். எங்களுக்கு வழித்துணையாயிரும். உமது பரிவன்பினால் எம் உள்ளங்களில் அன்பை மூட்டி நம்பிக்கை உயர்வடையச் செய்தருளும். இதனால் நாங்கள் சகோதரர் சகோதரிகளோடு ஒன்றித்து மறைநூல்களிலும் அப்பம் பிடுவதிலும் உம்மைக் கண்டுகொள்வோமாக. உம்மோடு3…'),
('W4-2Tue-1L', 'ஆண்டவரே. உமது நம்பிக்கையெனும் கொடையை எம்மில் அதிகரித்தருளும். இதனால் நாங்கள் உமக்கு அளிக்கும் புகழ்ச்சி, விண்ணக நலன்களை எங்களில் பெருகச் செய்வதாக. உம்மோடு…'),
('W4-2Tue-5V', 'ஆண்டவரே, உமது திருமுன் நாங்கள் இறைவேண்டல் செய்யும் இவ்வேளையில், நாங்கள் நாவினால் அறிக்கையிடுவதை எப்போதும் எங்கள் உள்ளத்திலிருத்தி சிந்திக்க அருளைத் தரும்படி உமது இரக்கத்தை கெஞ்சி கேட்கிறோம். உம்மோடு…'),
('W4-3Wed-1L', 'ஆண்டவரே, செம்மறியின் இரத்தத்தால் புதுப்பிக்கப்பட்டுப் புனிதப்படுத்தப்பட்ட உமது உடன்படிக்கையை நினைவுகூர்ந்தருளும். இதனால் உம் மக்கள் தங்கள் பாவங்களுக்கு மன்னிப்பு அடைந்து அருள்வாழ்வில் எந்நாளும் வளரச் செய்தருளும். உம்மோடு…'),
('W4-3Wed-5V', 'ஆண்டவரே, பசித்தோரை வானக உணவினால் நிரப்புகின்றீர்; இரக்கத்துடன் உம் மக்களை இப்பொழுது நினைவுகூர்ந்து, உமது செல்வப் பெருக்கால் எங்களது ஏழ்மையைப் போக்கியருளும். உம்மோடு…'),
('W4-4Thu-1L', 'ஆண்டவரே, உமது மீட்பைப்பற்றிய உண்மை அறிவை எமக்கு அளித்தருளும்; அதனால் நாங்கள் பயத்திலிருந்தும் எங்கள் பகைவர்களின் பிடியிலிருந்தும் விடுதலைபெற்று, எங்கள் வாழ்நாளெல்லாம் உண்மையுடன் உமக்கு பணி புரிவோமாக. உம்மோடு…'),
('W4-4Thu-5V', 'ஆண்டவரே, எங்கள் மாலைப் புகழுக்கு மனமுவந்து செவிசாய்த்தருளும்; உமது மகனின் முன்மாதிரியைப் பின்பற்றவும் எங்கள் பணிகளில் சோர்ந்துபோகாமல் நற்பயனைக் கொண்டுவரவும் எங்களுக்கு அருள் கூர்ந்தருளும். உம்மோடு…'),
('W4-5Fri-1L', 'ஆண்டவராகிய கடவுளே, இறைவேண்டலில் ஒன்றுபட்டிருக்கும் எங்களுக்கு உமது அருளை ஏராளமாய் வழங்குவீராக. உமது கட்டளைகளின் வழியில் நாங்கள் நடக்க நீர் எங்களில் செயலாற்றுவதன் வழியாக, நாங்கள் இவ்வுலகில் ஆறுதலையும் மறுவுலகில் நிலைமகிழ்ச்சியும் அடைவோமாக. உம்மோடு…'),
('W4-5Fri-5V', 'எல்லாம் வல்ல இரக்கமுள்ள கடவுளே! உம் மகன் இயேசு கிறிஸ்து உலகை மீட்பதற்காகப் பாடுபட வேண்டுமெனத் திருவுளமானீர். உமது மக்கள் தங்களையே உமக்கு உயிருள்ள பலியாக ஒப்புக்கொடுத்து, உமது அன்பின் நிறைவை அடைய அருள்புரிவீராக. உம்மோடு2…'),
('W4-6Sat-1L', 'என்றும் வாழும் எல்லாம் வல்ல கடவுளே. உண்மை ஒளி, நிலைவாழ்வு இவற்றிக் பெருஞ்சுடரே, இக்காலை வேளையில் பாவ இருளை அகற்றி, உமது திருவருகையின் மகிமையால் எம் உள்ளங்களை நிரப்பியருளும். உம்மோடு…');
