INSERT INTO `text__scripture` (`refKey`, `Content`) VALUES
('1அர2:2ஆ-3', 'நீ நெஞ்சுறுதியும் வீரமும் கொண்டவனாயிரு. உன் கடவுளாகிய ஆண்டவரின் ஆணைகளைக் கடைப்பிடி. அவர் காட்டும் வழியில் நட. மோசேயின் சட்ட நூலில் எழுதப்பட்டுள்ள கடவுளுடைய நியமங்கள், விதிமுறைகள் நீதிச் சட்டங்கள், ஒழுங்குமுறைகள் ஆகியவற்றைக் கடைப்பிடி இப்படிச் செய்தால், நீ செய்யும் காரியங்களிலும் செல்லும் இடங்களிலும் வெற்றி காண்பாய்.'),
('1கொரி1:23-24', ''),
('1கொரி12:12-13', 'உடல் ஒன்றே, உறுப்புகள் பல. உடலின் உறுப்புகள் பலவாயினும் உடல் ஒன்றாயிருப்பதுபோல கிறிஸ்துவும் இருக்கிறார். ஏனெனில், யூதரானாலும் கிரேக்கரானாலும் அடிமைகளானாலும் உரிமைக் குடிமக்களானாலும் நாம் எல்லாரும் ஒரே தூய ஆவியால் ஒரே உடலாய் இருக்கும்படி திருமுழுக்குப் பெற்றோம். அந்த ஒரே ஆவியையே பானமாகவும் பெற்றோம்.'),
('1கொரி13:8-9,13', 'இறைவாக்கு உரைக்கும் கொடை ஒழிந்துபோம்; பரவசப்பேச்சு பேசும் கொடையும் ஓய்ந்துபோம்; அறிவும் அழிந்துபோம். ஆனால் அன்பு ஒருபோதும் அழியாது. ஏனெனில், நமது அறிவு அரைகுறையானது; நாம் அரை குறையாகவே இறைவாக்கும் உரைக்கிறோம். ஆக, நம்பிக்கை, எதிர்நோக்கு, அன்பு ஆகிய மூன்றுமே நிலையாய் உள்ளன. இவற்றுள் அன்பே தலைசிறந்தது.'),
('1கொரி15:1-2அ,3-4', ''),
('1கொரி15:55-57', ''),
('1கொரி2:7-10அ', 'வெளிப்படுத்தப்படாமல் மறைபெருளாய் இருக்கும் இறை ஞானத்தைப்பற்றியே நாங்கள் பேசுகிறோம். அது நாம் மேன்மை பெற வேண்டும் என்னும் நோக்குடன் உலகம் தோன்றும் முன்பே, கடவுளின் திட்டத்தில் இருந்தது. இவ்வுலகத் தலைவர்கள் எவரும் அதை அறிந்து கொள்ளவில்லை. அறிந்திருந்தால், அவர்கள் மாட்சிக்குரிய ஆண்டவரைச் சிலுவையில் அறைந்திருக்க மாட்டார்கள். ஆனால் மறைநூலில் எழுதியுள்ளவாறு, “தம்மிடம் அன்பு கொள்ளுகிறவர்களுக்கென்று கடவுள் ஏற்பாடு செய்தவை கண்ணுக்குப் புலப்படவில்லை; செவிக்கு எட்டவில்லை; மனித உள்ளமும் அதை அறியவில்லை.” இதைக் கடவுள் தூய ஆவியாரின் வழியாக நமக்கு வெளிப்படுத்தினார்.'),
('1தெச14:9ஆ', ''),
('1தெச2:13', 'கடவுளின் வார்த்தையை நீங்கள் எங்களிடமிருந்து கேட்டபோது, அதை மனித வார்த்தையாக அல்ல, கடவுளின் வார்த்தையாகவே ஏற்றுக்கொண்டீர்கள். இதற்காக நாங்கள் கடவுளுக்கு இடைவிடாது நன்றி கூறுகிறோம். உண்மையாகவே அது கடவுளுடைய வார்த்தைதான்; அதுவே நம்பிக்கை கொண்ட உங்களில் செயலாற்றுகிறது.'),
('1தெச3:12-13', 'உங்கள் மீது நாங்கள் கொண்ட அன்பு வளர்ந்து பெருகுவதுபோல, நீங்கள் ஒருவர் ஒருவருக்காகவும் எல்லாருக்காகவும் கொண்டுள்ள அன்பையும் ஆண்டவர் வளர்த்துப் பெருகச் செய்வாராக! இவ்வாறு நம் ஆண்டவர் இயேசு தம்முடைய தூயோர் அனைவரோடும் வரும் பொழுது, நம் தந்தையாம் கடவுள்முன் நீங்கள் குற்றமின்றித் தூய்மையாக இருக்குமாறு அவர் உங்கள் உள்ளங்களை உறுதிப்படுத்துவாராக.'),
('1தெச4:14', ''),
('1தெச5:23', ''),
('1தெச5:4-5', 'அன்பர்களே! நீங்கள் இருளில் நடப்பவர்களல்ல; ஆகவே அந்த நாள் திருடனைப்போல் உங்களுக்கு வராது. நீங்கள் எல்லாரும் ஒளியைச் சார்ந்தவர்கள்; பகலில் நடப்பவர்கள் நாம் இரவையோ இருளையோ சார்ந்தவரல்ல.'),
('1தெச5:9-10', 'கடவுள் நம்மைத் தம் சினத்துக்கு ஆளாவதற்கு அல்ல, நம் ஆண்டவர் இயேசு கிறிஸ்து வழியாக மீட்பு அடையவே ஏற்படுத்தியுள்ளார். நாம் இருந்தாலும் இறந்தாலும் அவரோடு இணைந்து வாழும் வண்ணம் அவர் நம் பொருட்டு இறந்தார்.'),
('1பேது1;15-16', 'உங்களை அழைத்தவர் தூய்மையுள்ளவராய் இருப்பது போல நீங்களும் உங்கள் நடத்தையிலெல்லாம் தூய்மையுள்ளவர்களாய் இருங்கள். ‘நீங்கள் தூயவராயிருங்கள்; ஏனெனில், நான் தூயவன்’ என மறைநூலில் எழுதப்பட்டிருக்கிறது.'),
('1பேது1:22-23', 'உண்மைக்குக் கீழ்ப்படிந்து, உங்கள் ஆன்மா தூய்மை அடைந்துள்ளதால் நீங்கள் வெளிவேடமற்ற முறையில் சகோதர அன்பு காட்ட முடியும். எனவே, நீங்கள் தூய உள்ளத்தோடு ஒருவருக்கொருவர் ஆழ்ந்த அன்பு காட்டுங்கள். நீங்கள் அழியக்கூடிய வித்தினால் அல்ல; மாறாக, உயிருள்ள தும் நிலைத்திருப்பதுமான அழியா வித்தாகிய கடவுளின் வார்த்தையால் புதுப்பிறப்பு அடைந்துள்ளீர்கள்.'),
('1பேது1:3-5', 'நம் ஆண்டவர் இயேசு கிறிஸ்துவின் கடவுளும் தந்தையுமானவர் போற்றி! அவர் தம் பேரிரக்கத்தின்படி இறந்த இயேசு கிறிஸ்துவை உயிர்த்தெழச் செய்து, நமக்குப் புதுப்பிறப்பு அளித்துள்ளார். இவ்வாறு குன்றா எதிர்நோக்குடன் நாம் வாழ்கிறோம். அழியாத, மாசற்ற ஒழியாத உரிமைப் பேறும், உங்களுக்கென விண்ணுலகில் வைக்கப்பட்டுள்ளது. நீங்கள் கொண்டுள்ள நம்பிக்கையின் வழியாய், மீட்புக்காகக் கடவுளுடைய வல்லமையால் பாதுகாக்கப்பட்டு வருகிறீர்கள். இம்மீட்பு இறுதி காலத்தில் வெளிப்பட ஆயத்தமாய் உள்ளது.'),
('1பேது1:6-9', 'இப்போது சிறிது காலம், நீங்கள் பல்வகைச் சோதனைகளால் துயருறவேண்டியிருக்கும். அந்நாளிலே பேருவகை கொள்வீர்கள். அழியக்கூடிய பொன் நெருப்பினால் புடமிடப்படுகிறது. அதைவிட விலையுயர் உங்கள் நம்பிக்கையும் மெய்ப்பிக்கப்படவே துயருறுகிறீர்கள் இயேசு கிறிஸ்து வெளிப்படும்போது, அந்நம்பிக்கை உங்களுக்குப் புகழும் மாண்பும் பெருமையும் தருவதாய் விளங்கும். நீங்கள் அவரைப் பார்த்ததில்லை; எனினும் அவர்மீது அன்பு செலுத்துகிறீர்கள். இப்பொழுதும் நீங்கள் அவரைக் கண்டதில்லை; எனினும் நம்பிக்கை கொண்டு சொல்லொண்ணா, ஒப்பற்ற மகிழ்ச்சியடைந்து, பேருவகை கொள்கிறீர்கள். இவ்வாறு உங்கள் நம்பிக்கையின் குறிக்கோளான, ஆன்ம மீட்பையும் பெறுகிறீர்கள்.'),
('1பேது3:8-9', 'நீங்கள் எல்லாரும் ஒருமனப்பட்டிருங்கள். பிறரிடம் இரக்கமும் சகோதர அன்பும் பரிவுள்ளமும் மனத் தாழ்மையும் கொண்டிருங்கள். தீமைக்குப் பதில் தீமை செய்யாதீர்கள். பழிச்சொல்லுக்குப் பழிச்சொல் கூறாதீர்கள்; மாறாக, ஆசி கூறுங்கள். ஏனென்றால், கடவுள் வாக்களித்த ஆசியை உரிமையாக்கிக்கொள்வதற்கே அழைக்கப்பட்டிருக்கிறீர்கள்.'),
('1பேது4:10-11', 'நீங்கள் கடவுளுடைய பல்வகை அருள்கொடைகளின் சீரிய பொறுப்பாளர்கள். எனவே உங்களுள் ஒவ்வொருவரும் தாம் பெற்றுக்கொண்ட அருள்கொடையைப் பயன்படுத்தி, ஒருவருக்கொருவர் பணிபுரியுங்கள். ஒருவர் பேசும் கொடையைப் பெற்றிருந்தால், அவரது பேச்சு கடவுளுடைய வார்த்தைகளைப்போல் இருக்கட்டும். ஒருவர் பணி செய்யும் கொடையைப் பெற்றிருந்தால் கடவுள் அருளும் ஆற்றலைப் பெற்றவர்போல் பணி செய்யட்டும்; இவ்வாறு இயேசு கிறிஸ்துவின் வழியாகக் கடவுள் அனைத்திலும் பெருமை பெறுவார். அவருக்கே மாட்சியும் வல்லமையும் என்றென்றும் உரித்தாகுக! ஆமென்.'),
('1பேது4:13-14', ''),
('1பேது5:1-4', ''),
('1பேது5:5ஆ-7', 'ஒருவர் மற்றவரோடு பழகும்போது எல்லாரும் மனத் தாழ்மையை ஆடையாய் அணிந்திருங்கள். ஏனெனில், “செருக்குற்றோரைக் கடவுள் இகழ்ச்சியுடன் நோக்குவார்; தாழ்நிலையில் உள்ளவர் களுக்கோ கருணை காட்டுவார்.” ஆகையால் கடவுளுடைய வல்லமைமிக்க கரத்தின்கீழ் உங்களைத் தாழ்த்துங்கள்; அப்பொழுது அவர் ஏற்ற காலத்தில் உங்களை உயர்த்துவார். உங்கள் கவலைகளை யெல்லாம் அவரிடம் விட்டு விடுங்கள் ஏனென்றால், அவர் உங்கள்மேல் கவலை கொண்டுள்ளார்.'),
('1பேது5:8-9அ', 'அறிவுத் தெளிவோடு விழிப்பாயிருங்கள். உங்கள் எதிரியாகிய அலகை யாரை விழுங்கலாமெனக் கர்ச்சிக்கும் சிங்கம்போலத் தேடித் திரிகிறது. அசையாத நம்பிக்கை கொண்டவர்களாய் அதனை எதிர்த்து நில்லுங்கள்.'),
('1யோவா2:3-6', 'கட்டளைகளை நாம் கடைப்பிடித்தால் நாம் அவரை அறிந்துகொண்டோம் என்பது உறுதியாகத் தெரியும். “அவரை எனக்குத் தெரியும்” எனச் சொல்லிக் கொண்டு அவருடைய கட்டளைகளைக் கடைப்பிடிக்காதோர் பொய்யர்; உண்மை அவர்களிடம் இராது. ஆனால் அவரது வார்த்தையைக் கடைப்பிடிப்போரிடம் கடவுளின் அன்பு உண்மையாகவே நிறைவடைகிறது; நாம் அவரோடு இணைந்து இருக்கிறோம் என அதனால் அறிந்து கொள்ளலாம். அவரோடு இணைந்திருப்பதாகக் கூறுவோர் அவர் வாழ்ந்தவாறே வாழக் கடமைப்பட்டவர்கள்.'),
('1யோவா3:16', 'கிறிஸ்து நமக்காகத் தம் உயிரைக் கொடுத்தார். இதனால் அன்பு இன்னதென்று அறிந்து கொண்டோம். ஆகவே நாமும் நம் சகோதரர் சகோதரிகளுக்காக உயிரைக் கொடுக்கக் கடமைப்பட்டிருக்கிறோம்.'),
('1யோவா3:1அ-2', 'நம் தந்தை நம்மிடம் எத்துணை அன்பு கொண்டுள்ளார் என்று பாருங்கள். நாம் கடவுளின் மக்களென அழைக்கப்படுகிறோம்; நாம் கடவுளின் மக்களாகவே இருக்கிறோம். என் அன்பார்ந்தவர்களே, இப்போது நாம் கடவுளின் பிள்ளைகளாய் இருக்கிறோம் இனி எத்தன்மையராய் இருப்போம் என்பது இன்னும் வெளிப்படவில்லை. ஆனால், அவர் தோன்றும்போது நாமும் அவரைப்போல் இருப்போம்; ஏனெனில், அவர் இருப்பதுபோல் அவரைக் காண்போம்.'),
('1யோவா4:14-15', 'தந்தை தம் மகனை உலகிற்கு மீட்பராக அனுப்பினார் என்பதை நாங்களே கண்டறிந்தோம்; சான்றும் பகர்கின்றோம். இயேசுவே இறைமகன் என ஏற்று அறிக்கையிடுபவரோடு கடவுள் இணைந்திருக்கிறார்; அவரும் கடவுளோடு இணைந்திருக்கிறார்.'),
('2கொரி1:3-4', 'நம் ஆண்டவர் இயேசு கிறிஸ்துவின் தந்தை இரக்கம் நிறைந்த கடவுள். அவரே ஆறுதல் அனைத்துக்கும் ஊற்று; அவரை நாம் போற்றுவோம். கடவுள் எங்களுடைய இன்னல்கள் அனைத்திலும் எங்களுக்கு ஆறுதல் அளிக்கிறார். நாங்களே, கடவுளிடமிருந்து ஆறுதல் பெற்றுள்ளதால் பல்வேறு இன்னல்களில் உழலும் மற்ற மக்களுக்கும் ஆறுதல் அளிக்க எங்களால் முடிகிறது.'),
('2கொரி1:3-5', ''),
('2கொரி12:9ஆ-10', 'நான் என் வலுவின்மையைப் பற்றித்தான் மனமுவந்து பெருமை பாராட்டுவேன். அப்போது கிறிஸ்துவின் வல்லமை என்னுள் தங்கும். ஆகவே என் வலுவின்மையிலும் இகழ்ச்சியிலும் இடரிலும் இன்னலிலும் நெருக்கடியிலும் கிறிஸ்துவை முன்னிட்டு நான் அகமகிழ்கிறேன். ஏனெனில் நான் வலுவற்றிருக்கும்போது வல்லமை பெற்றவனாக இருக்கிறேன்.'),
('2கொரி13:4', 'கிறிஸ்து வலுவற்றவராய்ச் சிலுவையில் அறையப்பட்டார். ஆனால் கடவுளின் வல்லமையினால் அவர் உயிர் வாழ்கிறார். அவருடைய வலுவின்மையில் பங்குபெறும் நாங்களும் உங்கள் பொருட்டுக் கடவுளின் வல்லமையால் அவரோடு வாழ்வோம்.'),
('2கொரி6:16;71', ''),
('2திமொ2:10-12அ', ''),
('2திமொ2:8,11-13', 'தாவீதின் மரபில் வந்த இயேசு கிறிஸ்து இறந்து உயிர் பெற்று எழுந்தார் என்பதே என் நற்செய்தி. இதனை நினைவில் கொள். பின்வரும் கூற்று நம்பத் தகுந்தது: நாம் அவரோடு இறந்தால், அவரோடு வாழ்வோம் அவரோடு நிலைத்திருந்தால், அவரோடு ஆட்சி செய்வோம்; நாம் அவரை மறுதலித்தால் அவர் நம்மை மறுதலிப்பார். நாம் நம்பத்தகாதவரெனினும் அவர் நம்பத்தகுந்தவர். ஏனெனில், தம்மையே மறுதலிக்க அவரால் இயலாது. இவற்றை நீ அவர்களுக்கு நினைவுறுத்து.'),
('2தெச2:13-14', 'ஆண்டவரால் அன்புகூரப்பட்ட சகோதர சகோதரிகளே! நாங்கள் கடவுளுக்கு உங்கள் பொருட்டு என்றும் நன்றி செலுத்தக் கடமைப்பட்டிருக்கிறோம். ஏனெனில், தூய ஆவியால் தூய்மையாக்கப்பட்டு, நீங்கள் உண்மையை நம்பி மீட்பு அடையும்படி, கடவுள் உங்களை முதன் முதலாகத் தேர்ந்துகொண்டார். நம் ஆண்டவர் இயேசு கிறிஸ்துவின் மாட்சியை நீங்கள் அடையும் பொருட்டே, நாங்கள் அறிவித்த நற்செய்தியின் வழியாக அவர் உங்களை அழைத்தார்.'),
('2தெச3:10ஆ-13', '‘உழைக்க மனமில்லாத எவரும் உண்ணலாகாது’ என்று நாங்கள் உங்களிடையே இருந்தபோதே, உங்களுக்குக் கட்டளை கொடுத்திருந்தோம். உங்களுள் சிலர் எந்த வேலையும் செய்யாமல், சோம்பேறிகளாகச் சுற்றித்திரிந்து, பிறர் வேலையில் தலையிடுவதாகக் கேள்விப்படுகிறோம். இத்தகையோர் ஒழுங்காகத் தங்கள் வேலையைச் செய்து, தாங்கள் உண்ணும் உணவுக்காக உழைக்க வேண்டும் என ஆண்டவர் இயேசு கிறிஸ்துவின் பெயரால் கட்டளையிட்டு அறிவுறுத்துகிறோம். சகோதர சகோதரிகளே! நன்மை செய்வதில் நீங்கள் மனந்தளர வேண்டாம்.'),
('2பேது1:10-11', 'சகோதர சகோதரிகளே, நீங்கள் அழைக்கப்பட்டவர்கள்; தெரிந்துகொள்ளப்பட்டவர்கள்; இந்த நிலையில் உறுதியாக நிற்க முழு முயற்சி செய்யுங்கள். இவ்வாறு செய்தால் நீங்கள் ஒருபோதும் தடுமாறமாட்டீர்கள். அப்பொழுது நம் ஆண்டவரும் மீட்பருமாகிய இயேசு கிறிஸ்துவின் என்றும் நிலைக்கும் ஆட்சியில் பங்கேற்கும் உரிமை உங்களுக்கு நிறைவாக அருளப்படும்.'),
('2பேது1:19-21', 'இறைவாக்கினர் கூறியது இன்னும் உறுதியாயிற்று. அவர்கள் கூறியதை நீங்கள் கருத்திற்கொள்வது நல்லது; ஏனெனில், பொழுது புலர்ந்து விடிவெள்ளி உங்கள் இதயங்களில் தோன்றும்வரை அது இருண்ட இடத்தில் ஒளிரும் விளக்கைப் போன்றது. ஆனால் மறைநூலிலுள்ள எந்த இறைவாக்கும் எவரது சொந்த விளக்கத்திற்கும் உட்பட்டது அல்ல என்பதை நீங்கள் முதலில் அறிய வேண்டும். தூய ஆவியால் ஆட்கொள்ளப்பட்ட மனிதர்கள் கடவுள் அருளியதை உரைத்ததே இறைவாக்கு. அது ஒருபோதும் மனித விருப்பத்தால் உண்டானது அல்ல.'),
('2பேது3:13-15அ', 'அவர் வாக்களித்தபடியே நீதி குடி கொண்டிருக்கும் புதிய விண்ணுலகும் புதிய மண்ணுலகும் வரும் என நாம் எதிர்பார்த்துக்கொண்டிருக்கிறோம். ஆகவே அன்பார்ந்தவர்களே, இவற்றை எதிர் பார்த்திருக்கும் உங்களை அவர் மாசுமறுவற்றவர்களாய், நல்லுறவு கொண்டவர்களாய்க் காணும் வகையில் முழு முயற்சி செய்யுங்கள். நம் ஆண்டவரின் பொறுமையை மீட்பு எனக கருதுங்கள்.'),
('lordsprayer', 'விண்ணுலகில் இருக்கிற எங்கள் தந்தையே,§உமது பெயர் தூயது எனப் போற்றப் பெறுக!§உமது ஆட்சி வருக!§உமது திருவுளம் விண்ணுலகில் நிறைவேறுவது போல,§மண்ணுலகிலும் நிறைவேறுக! §எங்கள் அன்றாட உணவை§இன்று எங்களுக்குத் தாரும்.§எங்களுக்கு எதிராகக் குற்றம் செய்வோரை§நாங்கள் மன்னிப்பது போல,§எங்கள் குற்றங்களை மன்னியும்.§எங்களைச் சோதனைக்கு உட்படுத்தாதேயும்.§தீயோனிடமிருந்து எங்களை விடுவித்தருளும்.§ஆமென்.'),
('ஆமோ5;8', 'அவரே கார்த்திகை, மிருகசீரிடம் ஆகிய விண்மீன்களை உண்டாக்கியவர்; காரிருளைக் காலை பொழுதாகச் செய்பவர்; பகற்பொழுதை இரவு வேளையாய் மாற்றுபவர்; கடல் நீரை அழைத்து நிலத்தின்மேல் பொழியச் செய்பவர்; அவரது பெயர் ‘ஆண்டவர்’.'),
('இச10:12-13', 'இஸ்ரயேலரே, உங்கள் கடவுளாகிய ஆண்டவருக்கு அஞ்சி, அவர்தம் அனைத்து வழிகளிலும் நடந்து, அவர்மீது அன்புகூர்ந்து, உங்கள் முழு இதயத்தோடும் உங்கள் முழு உள்ளத்தோடும் அவருக்குப் பணிபுரிந்து, உங்களுக்கு எல்லாம் நலமாகும் பொருட்டு நான் இன்று கற்பிக்கின்ற அவர் தம் கட்டளைகளையும் நியமங்களையும் நீங்கள் கடைப்பிடிக்க வேண்டுமென்பதன்றி அவர் உங்களிடமிருந்து வேறு என்ன எதிர்பார்க்கின்றார்?'),
('இச15:7-8', 'உன் கடவுளாகிய ஆண்டவர் உனக்குக் கொடுக்கும் நாட்டிலுள்ள எந்த நகரிலாவது உன் சகோதரன் ஒருவன் வறியவனாய் இருந்தால், உன் வறிய சகோதரன் மட்டில் உன் உள்ளத்தைக் கடினப்படுத்தாதே, உன் கையை மூடிக்கொள்ளாதே. மாறாக, அவனுக்கு உன் கரங்களைத் தாராளமாகத் திறந்து, அவன் தேவைக்கு ஏற்ப எவ்வளவு தேவையானாலும் கடன் கொடு.'),
('இச30:11,14', 'இன்று நான் உனக்குக் கட்டளையிடும் இந்தக் கட்டளை உனக்கு புரியாதது இல்லை; உன்னிடமிருந்து வெகு தொலையிலும் இல்லை. ஆனால், நீ அதை நிறைவேற்றுமாறு வார்த்தை உனக்கு மிக அருகில் உள்ளது. உன் வாயில், உன் இதயத்தில் உள்ளது.'),
('இச4:39அ–40ஆ', '‘மேலே விண்ணிலும் கீழே மண்ணிலும் ஆண்டவரே கடவுள் அவரைத் தவிர வேறு எவரும் இலர்’ என இன்று அறிந்து, உங்கள் உள்ளத்தில் இருத்துங்கள். நான் இன்று உங்களுக்குக் கட்டளையிடும் அவரது நியமங்களையும் கட்டளைகளையும் பின்பற்றுங்கள்.'),
('இச4:7', 'நாம் குரல் எழுப்பும் போதெல்லாம் நம் கடவுளாகிய ஆண்டவர் நம்மோடு உள்ளார். அவரைப் போல் மக்களுக்கு மிகவும் நெருங்கிய கடவுளைக் கொண்ட வேறு பேரினம் ஏதாகிலும் உண்டா?'),
('இச6:4-7', ''),
('உரோ11:31-36', 'கடவுளின் அருள் செல்வம், எத்துணை மிகுதியானது! அவருடைய ஞானமும் அறிவும், எத்துணை ஆழமானவை! அவருடைய தீர்ப்புகள் அறிவுக்கு எட்டாதவை! அவருடைய செயல்முறைகள், ஆராய்ச்சிக்கு அப்பாற்பட்டவை! “ஏனெனில், ஆண்டவரின் மனத்தை அறிபவர் யார்? அவருக்கு அறிவுரையாளராய் இருப்பவர் யார்? தமக்குக் கைம்மாறாக ஏதாவது கிடைக்கும் என, முன்னதாகவே அவரிடம் கொடுத்து வைத்தவர் யார்?” அனைத்தும் அவரிடமிருந்தே வந்தன; அவராலேயே உண்டாயின; அவருக்காகவே இருக்கின்றன, அவருக்கே என்றென்றும் மாட்சி உரித்தாகுக! ஆமென்.'),
('உரோ12:14-16அ', 'உங்களைத் துன்புறுத்துவோருக்கு ஆசி கூறுங்கள்; ஆம், ஆசி கூறுங்கள்; சபிக்க வேண்டாம். மகிழ்வாரோடு மகிழுங்கள்; அழுவாரோடு அழுங்கள். நீங்கள் ஒரு மனத்தவராய் இருங்கள்; உயர்வு மனப்பான்மை கொள்ளாமல், தாழ்நிலையில் உள்ளவர்களோடு நன்கு பழகுங்கள்.'),
('உரோ12:9-12', 'உங்கள் அன்பு கள்ளமற்றதாய் இருப்பதாக! தீமையை வெறுத்து நன்மையையே பற்றிக்கொள்ளுங்கள். உடன் பிறப்புகள் போன்று, ஒருவருக்கொருவர் உளங்களிந்த அன்பு காட்டுங்கள்; பிறர் உங்களை விட மதிப்புக்கு உரியவரென எண்ணுங்கள். விடாமுயற்சியோடும் ஆர்வமிக்க உள்ளத்தோடும் ஆண்டவருக்குப் பணி புரியுங்கள். எதிர்நோக்கி இருப்பதில் மகிழ்ச்சி கொள்ளுங்கள்; துன்பத்தில் தளரா மனத்துடன் இருங்கள் இறை வேண்டலில் நிலைத்திருங்கள்.'),
('உரோ13.11ஆ-13அ', 'உறக்கத்தினின்று விழித்தெழும் நேரம் ஏற்கெனவே வந்துவிட்டது. நாம் கிறிஸ்துவின்மேல் நம்பிக்கை கொண்டபோது இருந்ததைவிட, மீட்பு இப்பொழுது மிக அண்மையில் உள்ளது. இரவு முடியப்போகிறது; பகல் நெருங்கி உள்ளது. ஆகவே இருளின் ஆட்சிக்குரிய செயல்களைக் களைந்து விட்டு, ஒளியின் ஆட்சிக்குரிய படைக்கலங்களை அணிந்து கொள்வோமாக! பகலில் நடப்பதுபோல, மதிப்போடு நடந்து கொள்வோமாக!'),
('உரோ14:17-19', 'இறையாட்சி என்பது நாம் உண்பதையும் குடிப்பதையும் அடிப்படையாகக் கொண்டதல்ல; மாறாகத் தூய ஆவி அருளும் நீதி, அமைதி, மகிழ்ச்சி ஆகியவற்றையே அடிப்படையாகக் கொண்டது. இத்தகைய இறையாட்சி மனப்பான்மையோடு கிறிஸ்துவுக்குப் பணிபுரிவோர். கடவுளுக்கு உகந்தோராயும் மக்களின் மதிப்புக்கு உரியோராயும் இருப்பர். ஆகையால், அமைதிக்கு வழி வகுப்பவற்றை நாடுவோமாக! ஒருவர் மற்றவருக்கு வளர்ச்சி தருபவற்றைச் செய்ய முயலுவாமாக!'),
('உரோ15:1-3', 'மன வலிமை கொண்டவர்களாகிய நாம் வலுவற்றைவர்களின் குறைபாடுகளைத் தாங்கிக்கொள்ளக் கடமைப்பட்டிருக்கிறோம்; நமக்கு உகந்ததையே தேடலாகாது. அடுத்தவருடைய நன்மைக்காகவும் வளர்ச்சிக்காகவும் செயல்பட்டு அவர்களுக்கு உகந்தவர்களாக வாழுங்கள். கிறிஸ்துவும் தமக்கு உகந்ததைத் தேடவில்லை. “உம்மைப் பழித்துப் பேசினவர்களின் பழிச்சொற்கள் என்மீது விழுந்தன” என்று மறைநூலில் எழுதியுள்ளது அல்லவா!'),
('உரோ15:5-7', 'கிறிஸ்து இயேசுவின் முன்மாதிரிக்கேற்ப நீங்கள் ஒரே மனத்தினராய் இருக்குமாறு மன உறுதியையும் ஊக்கத்தையும் தரும் கடவுள் உங்களுக்கு அருள்புரிவாராக! இவ்வாறு நீங்கள் அனைவரும் ஒருமனப்பட்டு, நம் ஆண்டவர் இயேசு கிறிஸ்துவின் கடவுளும் தந்தையுமானவரை ஒரு வாய்ப்படப் போற்றிப் புகழ்வீர்கள். ஆகையால், கிறிஸ்து உங்களை ஏற்றுக்கொண்டது போல நீங்களும் ஒருவரை ஒருவர் ஏற்றுக்கொள்ளுங்கள். அப்போது கடவுளைப் பெருமைப்படுத்துவீர்கள். என் கருத்து இதுவே.'),
('உரோ3:21-22அ', 'இப்பொழுதோ கடவுள் மனிதரைத் தமக்கு ஏற்புடையவராக்கும் செயலுக்கும் திருச்சட்டத்துக்கும் தொடர்பில்லை என்பது வெளியாக்கப்பட்டுள்ளன. திருச்சட்டமும் இறைவாக்குகளும் இதற்குச் சான்று பகர்கின்றன. இயேசு கிறிஸ்துவின்மீது கொள்ளும் நம்பிக்கையின் வழியாகக் கடவுள் மனிதரைத் தமக்கு ஏற்புடையவராக்குகிறார்; நம்பிக்கை கொள்வோர் அனைவரையுமே அவர் ஏற்புடையவராக்குகிறார்.'),
('உரோ3:23-25அ', 'எல்லாருமே பாவம் செய்து கடவுள் கொடுத்த மேன்மையை இழந்து போயினர். ஆயினும் அனைவரும் கிறிஸ்து இயேசு நிறைவேற்றிய மீட்புச் செயலின்மூலம் கடவுளுடைய அருளால் இலவசமாய் அவருக்கு ஏற்புடையவர்கள் ஆக்கப்படுகின்றனர். இரத்தம் சிந்தி மனிதருடைய பாவத்துக்குக் கழுவாய் ஆகுமாறு இயேசுவைக் கடவுள் நியமித்தார்.'),
('உரோ6:22', 'இப்பொழுது நீங்கள் பாவத்தினின்று விடுதலை பெற்றுக் கடவுளுக்கு அடிமைகள் ஆகிவிட்டீர்கள்; இதனால் நீங்கள் காணும் பயன் தூய வாழ்வு. இதன் முடிவு நிலைவாழ்வு.'),
('உரோ8:1-2', 'கிறிஸ்து இயேசுவோடு இணைந்திருப்போருக்கு இனித் தண்டனைத் தீர்ப்பே கிடையாது. ஏனெனில், கிறிஸ்து இயேசுவோடு இணைந்திருப்போருக்கு வாழ்வு தரும் தூய ஆவியின் சட்டம் பாவம், சாவு என்பவற்றுக்கு உள்ளாக்கும் சட்டத்தினின்று என்னை விடுவித்துவிட்டது.'),
('உரோ8:18-21', 'இக்காலத்தில் நாம் படும் துன்பங்கள் எதிர்காலத்தில் நமக்காக வெளிப்படப் போகிற மாட்சியோடு ஒப்பிடத் தகுதியற்றவை என நான் எண்ணுகிறேன். இம்மாட்சியுடன் கடவுளின் மக்கள் வெளிப்படுவதைக் காண்பதற்காகப் படைப்பே பேராவலோடு காத்திருக்கிறது. ஏனெனில், படைப்பு பயனற்ற நிலைக்கு உட்பட்டுள்ளது; தானே விரும்பியதால் அப்படி ஆகவில்லை; அதை உட்படுத்தினவரின் விருப்பத்தால் அவ்வாறு ஆயிற்று; எனினும் அது எதிர்நோக்கை இழந்த நிலையில் இல்லை. அது அழிவுக்கு அடிமைப்பட்டிருக்கும் நிலையிலிருந்து விடுவிக்கப்பட்டு, கடவுளின் பிள்ளைகளுக்குரிய பெருமையையும் விடுதலையையும் தானும் பெற்றுக் கொள்ளும் என்கிற எதிர் நோக்கோடு இருக்கிறது.'),
('உரோ8:22-23', 'இந்நாள்வரை படைப்பு அனைத்தும் ஒருங்கே பேறுகால வேதனையுற்றுத் தவிக்கின்றது என்பதை நாம் அறிவோம். படைப்பு மட்டும் அல்ல; முதல் கொடையாகத் தூய ஆவியைப் பெற்றுக்கொண்டுள்ள நாமும் கடவுள் நம்மைத் தம் பிள்ளைகளாக்கப்போகும் நாளை, அதாவது நம் உடலை விடுவிக்கும் நாளை எதிர்நோக்கிப் பெருமூச்சு விடுகிறோம்.'),
('உரோ8:26', 'தூய ஆவியார் நமது வலுவற்ற நிலையில் நமக்குத் துணை நிற்கிறார்; ஏனெனில், எதற்காக, எப்படி நாம் இறைவனிடம் வேண்டுவது என்று நமக்குத் தெரியாது; தூய ஆவியார் தாமே சொல்வடிவம் பெற முடியாத நம்முடைய பெருமூச்சுகளின் வாயிலாய் நமக்காகப் பரிந்துபேசுகிறார்.'),
('உரோ8:35,37', 'கிறிஸ்துவின் அன்பிலிருந்து நம்மைப் பிரிக்கக் கூடியது எது? வேதனையா நெருக்கடியா? இன்னலா பட்டினியா? ஆடையின்மையா? இடரா? சாவா? எதுதான் நம்மைப் பிரிக்க முடியும்? ஆயினும், நம்மேல் அன்பு கூர்ந்தவரின் செயலால் மேற்கூறியவை அனைத்திலும் நாம் வெற்றிமேல் வெற்றி அடைகிறோம்.'),
('உரோ8:35,37-39', ''),
('உரோ9:4-5', 'அவர்கள்தாம் இஸ்ரயேல் மக்கள்; அவர்களையே கடவுள் தம் மக்களாக ஏற்றுக்கொண்டார். அவர்கள் நடுவில்தான் கடவுள் தம் மாட்சியை விளங்கச் செய்தார். உடன்படிக்கைளும் திருச்சட்டமும் திருவழிபாடும் வாக்குறுதிகளும் அவர்களிடமே ஒப்படைக்கப்பட்டன. குலமுதல்வர்களின் வழிவந்தவர்கள் அவர்கள்; மனிதர் என்னும் முறையில் கிறிஸ்துவும் அவர்களிடமிருந்தே தோன்றினார். இவரே எல்லாவற்றுக்கும் மேலான கடவுள்; என்றென்றும் போற்றுதற்குரியவர். ஆமென்.'),
('எசா11:1-2', 'ஈசாய் என்னும் அடிமரத்திலிருந்து தளிர் ஒன்று துளிர் விடும்; அதன் வேர்களிலிருந்து கிளை ஒன்று வளர்ந்து கனி தரும். ஆண்டவரின் ஆவி அவர்மேல் தங்கியிருக்கும்; ஞானம் மெய்யுணர்வு, அறிவுரைத் திறன் ஆற்றல், நுண்மதி, ஆண்டவரைப்பற்றிய அச்ச உணர்வு இவற்றை அந்த ஆவி அவருக்கு அருளும்.'),
('எசா55:1', 'தாகமாய் இருப்பவர்களே, நீங்கள் அனைவரும் நீர்நிலைகளுக்கு வாருங்கள்; கையில் பணமில்லாதவர்களே, நீங்களும் வாருங்கள்; தானியத்தை வாங்கி உண்ணுங்கள்; வாருங்கள், காசு பணமின்றித் திராட்சை இரசமும் பாலும் வாங்குங்கள்.'),
('எசா55:8-9', 'என் எண்ணங்கள் உங்கள் எண்ணங்கள் அல்ல, உங்கள் வழிமுறைகள் என் வழிமுறைகள் அல்ல என்கிறார் ஆண்டவர். மண்ணுலகத்திலிருந்து விண்ணுலகம் மிக உயர்ந்து இருப்பதுபோல உங்கள் வழிமுறைகளைவிட என் வழிமுறைகளும் உங்கள் எண்ணங்களை விட என் எண்ணங்களும் மிக உயர்ந்திருக்கின்றன.'),
('எசா56:7', ''),
('எசா61:1-10', ''),
('எசா61:10', ''),
('எசா66:1-2', 'ஆண்டவர் கூறுவது இதுவே: விண்ணகம் என் அரியணை; மண்ணகம் என் கால்மணை: அவ்வாறிருக்க எத்தகைய கோவிலை நீங்கள் எனக்காகக் கட்டவிருக்கிறீர்கள்? எத்தகைய இடத்தில் நான் ஒய்வெடுப்பேன்? அனைத்தையும் என் கைகளே உண்டாக்கின; இவை யாவும் என்னால் உருவாகின என்கிறார் ஆண்டவர். எளியவரையும் உள்ளம் வருந்துபவரையும் என் சொல்லுக்கு அஞ்சுபவரையும் நான் கண்ணோக்குவேன்.'),
('எசே36:25-27', ''),
('எசே37:12ஆ-14', 'தலைவராகிய ஆண்டவர் கூறுவது இதுவே: என் மக்களே! இதோ நான் உங்கள் கல்லறைகளைத் திறக்கப் போகிறேன். உங்களை உங்கள் கல்லறைகளினின்று மேலே கொண்டு வருவேன். உங்களுக்கு இஸ்ரயேல் நாட்டைத் திரும்பக் கொடுப்பேன். அப்போது, என் மக்களே! நான் உங்கள் கல்லறைகளைத் திறந்து. உங்களை அவற்றிலிருந்து வெளிக்கொணர்கையில், நானே ஆண்டவர் என்பதை அறிந்து கொள்வீர்கள். என் ஆவியை உங்கள் மீது பொழிவேன். நீங்களும் உயிர் பெறுவீர்கள். நானும் உங்களை உங்கள் சொந்த நாட்டில் குடியமர்த்துவேன். ‘ஆண்டவராகிய நான் உரைத்தேன்: நானே இதைச் செய்தேன்’ என அப்போது அறிந்து கொள்வீர்கள் என்கிறார் தலைவராகிய ஆண்டவர்.'),
('எபி12:22-24', 'நீங்கள் வந்து சேர்ந்திருக்கும் சீயோன் மலை வாழும் கடவுளின் நகர்; விண்ணக எருசலேம். அதனைப் பல்லாயிரக்கணக்கான வானதூதர் சூழ்ந்துள்ளனர். விண்ணகத்தில் பெயர் எழுதப்பட்டுள்ள தலைப் பேறானவர்களின் திருச்சபை, விழாக் கூட்டமென அங்கே கூடியுள்ளது. நிறைவு பெற்ற நேர்மையாளர்களேடு சேர்ந்து, அனைவருக்கும் நடுவரான கடவுள் முன்னிலையிலும் புதியதோர் உடன்படிக்கையின் இணைப்பாளராகிய இயேசுவின் முன்னிலையிலும் நிற்கிறீர்கள். ஆபேலின் இரத்தத்தைப் போலன்றிச் சிறந்த முறையில் குரலெழுப்பும் இயேசுவின் இரத்தத்தினால் தெளிக்கப்பட்டிருக்கிறீர்கள்.'),
('எபி13:20-21', 'என்றுமுள்ள உடன்படிக்கையின் இரத்தத்தால் ஆடுகளின் பெரும் ஆயரான நம் ஆண்டவர் இயேசுவை இறந்தோரிடமிருந்து எழுப்பியவர், அமைதியை அருளும் கடவுளே. அவர் தம் திருவுளத்தை நீங்கள் நிறைவேற்றும்படி, எல்லா நன்மையும் செய்வதற்கு உங்களை ஆயத்தப்படுத்தி இயேசு கிறிஸ்து வழியாகத் தமக்கு உகந்ததை நம்மில் செய்தருள்வாராக! இயேசு கிறிஸ்துவுக்கே என்றென்றும் மாட்சி உரித்தாகுக! ஆமென்.'),
('எபி13:7-8', ''),
('எபி13:7-9அ', ''),
('எபி2:9-10', ''),
('எபே1:17-18', ''),
('எபே2:13-16', 'ஒரு காலத்தில் தொலையில் இருந்த நீங்கள் இப்பொழுது இயேசு கிறிஸ்துவோடு இணைந்து, அவரது இரத்தத்தின் மூலம் அருகில் கொண்டுவரப்பட்டிருக்கிறீர்கள். ஏனெனில், அவரே நமக்கு அமைதி அருள்பவர். அவரே இரண்டு இனத்தவரையும் பிரித்து நின்ற பகைமை என்னும் சுவரை, தமது உடலில் ஏற்ற துன்பத்தின் வழியாய்த் தகர்த்தெறிந்து, அவர்களை ஒன்றுபடுத்தினார். பல கட்டளைகளையும் விதிகளையும் கொண்ட யூதச் சட்டத்தை அழித்தார். இரு இனத்தவரையும் தம்மோடு இணைந்திருக்கும் புதியதொரு மனித இனமாகப் படைத்து அமைதி ஏற்படுத்தவே இவ்வாறு செய்தார். தாமே துன்புற்றுப் பகைமையை அழித்தார். சிலுவையின் வழியாக இரு இனத்தவரையும் ஒருடலாக்கிக் கடவுளோடு ஒப்புரவாக்க இப்படிச் செய்தார்.'),
('எபே2:19-22', ''),
('எபே3:20-21', 'நம்முள் வல்லமையோடு செயல்படுபவரும் நாம் வேண்டுவதற்கும் நினைப்பதற்கும் மிகவும் மேலாக அனைத்தையும் செய்ய வல்லவருமான கடவுளுக்கே திருச்சபையில் கிறிஸ்து இயேசு வழியாகத் தலைமுறை தலை முறையாக என் றென்றும் மாட்சி உரித்தாகுக! ஆமென்.'),
('எபே4:11-13', ''),
('எபே4:26-27', 'சினமுற்றாலும் பாவம் செய்யாதிருங்கள்; பொழுது சாய்வதற்குள் உங்கள் சினம் தணியட்டும். அலகைக்கு இடம் கொடாதீர்கள்.'),
('எபே4:29-32', 'கெட்ட வார்த்தை எதுவும் உங்கள் வாயினின்று வரக்கூடாது. கேட்போர் பயனடையும்படி, தேவைக்கு ஏற்றவாறு, அருள் வளர்ச்சிக்கேற்ற நல்ல வார்த்தைகளையே பேசுங்கள். கடவுளின் தூய ஆவியார்க்குத் துயரம் வருவிக்காதீர்கள். மீட்பு நாளை முன்னிட்டு உங்கள்மீது பொறிக்கப்பட்ட முத்திரையாக அவர் இருக்கிறார். மனக்கசப்பு, சீற்றம், சினம், கூச்சல் பழிச்சொல் எல்லாவற்றையும், தீமை அனைத்தையும் உங்களை விட்டு நீக்குங்கள் ஒருவருக்கொருவர் நன்மை செய்து பரிவு காட்டுங்கள்; கடவுள் உங்களைக் கிறிஸ்து வழியாக மன்னித்ததுபோல நீங்களும் ஒருவரை ஒருவர் மன்னியுங்கள்.'),
('எரே15:16', 'நான் உம் சொற்களைக் கண்டடைந்தேன்; அவற்றை உட்கொண்டேன்; உம் சொற்கள் எனக்கு மகிழ்ச்சி தந்தன. என் உள்ளத்திற்கு உவகை அளித்தன. ஏனெனில், படைகளின் ஆண்டவரே, உம் பெயரே எனக்கு வழங்கலாயிற்று.'),
('எரே17:9-10', 'இதயமே அனைத்திலும் வஞ்சகம் மிக்கது; அதனை நலமாக்க முடியாது, அதனை யார்தான் புரிந்து கொள்வர்? ஆண்டவராகிய நானே இதயச் சிந்தனைகளை ஆய்பவர்; உள்ளுணர்வுகளைச் சோதித்து அறிபவர். ஒவ்வொருவரின் வழிகளுக்கும் செயல்களின் விளைவுக்கும் ஏற்றவாறு நடத்துபவர்.'),
('எரே32;40', 'நான் அவர்களோடு என்றும் நிலைத்திருக்கு உடன்படிக்கை ஒன்றைச் செய்துகொள்வேன். எனனே அவர்களுக்கு நன்மை செய்ய நான் தவறமாட்டேன். என்னைப் பற்றிய அச்சத்தை அவர்களது இதயத்தில் பதியவைப்பேன். இதனால் அவர்கள் என்னை விட்டு விலகிச் செல்லமாட்டார்கள்.'),
('கலா2:19ஆ-20', 'கிறிஸ்துவோடு சிலுவையில் அறையப்பட்டிருக்கிறேன். எனவே, இனி வாழ்பவன் நான் அல்லன்; கிறிஸ்துவே என்னுள் வாழ்கிறார். இறைமகன்மீது கொண்டுள்ள நம்பிக்கையின் அடிப்படையில் நான் இவ்வுலகில் வாழ்கிறேன். இவரே என்மீது அன்புகூர்ந்தார். எனக்காகத் தம்மையே ஒப்புவித்தார்.'),
('கலா4:4-5', ''),
('கலா5:16-17', 'நான் சொல்கிறேன்; தூய ஆவியின் தூண்டுதலுக்கேற்ப வாழுங்கள்; அப்போது ஊனியல்பின் இச்சைகளை நிறைவேற்றமாட்டீர்கள். ஊனியல்பின் இச்சை தூய ஆவிக்கு முரணானது. தூய ஆவியின் விருப்பம் ஊனியல்புக்கு முரணானது. இவை ஒன்றுக்கொன்று எதிராய் உள்ளதால் நீங்கள் செய்ய விரும்புவதை உங்களால் செய்ய முடிவதில்லை.'),
('கலா5:26;6:2', 'கலா 5:26; 6:2§வீண் பெருமையைத் தேடாமலும், ஒருவருக்கு ஒருவர் எரிச்சல் ஊட்டாமலும், ஒருவர்மீது ஒருவர் பொறாமைப் படாமலும் இருப்போமாக! ஒருவர் மற்றவருடைய சுமைகளைத் தாங்கிக் கொள்ளுங்கள்; இவ்வாறு கிறிஸ்துவின் சட்டத்தை நிறைவேற்றுவீர்கள்.'),
('கலா6:8', 'தம் ஊனியல்பாகிய நிலத்தில் விதைப்போர் அந்த இயல்பிலிருந்து அழிவையே அறுவடை செய்வர். ஆவியாருக்குரிய வாழ்வாகிய நிலத்தில் விதைப்போர் அந்த ஆவியார் அருளும் நிலைவாழ்வை அறுவடை செய்வர்.'),
('கொலோ1:23', 'நீங்கள் நற்செய்தியைக் கேட்டுப் பெற்றுக்கொண்ட எதிர்நோக்கை இழந்துவிடாமல் நம்பிக்கையை அடித்தளமாகக் கொண்டு அதில் உறுதியாக நிலைத்திருங்கள். உலகெங்கும் படைப்பனைத்துக்கும் நற்செய்தி பறைசாற்றப்பட்டுள்ளது.'),
('கொலோ1:24-25', ''),
('கொலோ1:2ஆ-6அ', 'நம் தந்தையாம் கடவுளிடமிருந்து உங்களுக்கு அருளும் அமைதியும் உரித்தாகுக! உங்களுக்காக நாங்கள் வேண்டும் பொழுதெல்லாம், நம் ஆண்டவர் இயேசு கிறிஸ்துவின் தந்தையாம் கடவுளுக்கு நன்றி செலுத்துகிறோம். கிறிஸ்து இயேசுவின் மீது நீங்கள் கொண்டுள்ள நம்பிக்கை பற்றியும் இறைமக்கள் அனைவரிடமும் செலுத்தும் அன்பு பற்றியும் நாங்கள் கேள்வியுற்றோம். இவை இரண்டும் விண்ணகத்தில் உங்களுக்கென வைக்கப்பட்டிருக்கும் நன்மைகளை எதிர்நோக்குவதால் விளைந்தவை. நீங்கள் முன்பு கேட்ட உண்மையின் நற்செய்தி வழியாக அந்த எதிர்நோக்குப்பற்றி அறிந்து கொண்டீர்கள். உலகம் முழுவதும் பரவிப் பயனளித்து வரும் அந்நற்செய்தி உங்களை வந்தடைந்தது.'),
('கொலோ1:3-6', ''),
('கொலோ1:9ஆ-11', 'நீங்கள் முழு ஞானத்தையும் ஆவியாருக்குரிய அறிவுத்திறனையும் பெற்றுக் கடவுளின் திருவுளத்தைப் பற்றிய அறிவை நிறைவாக அடையவேண்டும். நீங்கள் அனைத்திலும் ஆண்டவருக்கு உகந்தவற்றைச் செய்து அவருக்கு ஏற்புடையவர்களாய் நடந்து கொள்ள வேண்டும். நீங்கள் பயன் தரும் நற்செயல்கள் அனைத்தும் செய்து கடவுளைப்பற்றிய அறிவில் வளர வேண்டும். நீங்கள் முழு மன உறுதியோடும் பொறுமையோடும் இருக்குமாறு தம் மாட்சிமிகு ஆற்றலுக்கேற்பத் தம் வல்லமையால் அவர் உங்களுக்கு வலுவூட்ட வேண்டும்.'),
('கொலோ3:16', 'கிறிஸ்துவைப் பற்றிய நற்செய்தி உங்களுக்குள் நிறைவாகக் குடிகொள்வதாக! முழு ஞானத்தோடு ஒருவருக்கு ஒருவர் கற்பித்து அறிவுரை கூறுங்கள். திருப்பாடல்களையும் புகழ்ப்பாக்களையும் ஆவிக்குரிய பாடல்களையும் நன்றியோடு உளமாரப் பாடிக் கடவுளைப் போற்றுங்கள்.'),
('கொலோ3:17', 'எதைச் சொன்னாலும் எதைச் செய்தாலும் அனைத்தையும் ஆண்டவர் இயேசுவின் பெயரால் செய்து அவர் வழியாய்த் தந்தையாம் கடவுளுக்கு நன்றி செலுத்துங்கள்.'),
('சாஞா1:1-2', 'மண்ணுலகை ஆள்பவர்களே, நீதியின்மேல்அன்பு செலுத்துங்கள்; நன்மனத்துடன் ஆண்டவரைப் பற்றி நினைந்திடுங்கள்; நேர்மையான உள்ளத்துடன் அவரைத் தேடுங்கள். அவரைச் சோதியாதோர் அவரைக் கண்டடைகின்றனர்; அவரை நம்பினோர்க்கு அவர் தம்மை வெளிப்படுத்துகின்றார்.'),
('சாஞா15:1,3', 'எங்கள் கடவுளே, நீர் பரிவும் உண்மையும் பொறுமையும் உள்ளவர்: அனைத்தையும் இரக்கத்துடன் ஆண்டு வருகின்றீர். உம்மை அறிதலே நிறைவான நீதி; உமது ஆற்றலை அறிதலே இறவாமைக்கு ஆணிவேர்.'),
('திப2:42-45', ''),
('திவெ1:4ஆ-5', ''),
('திவெ21:2-3, 22,27', ''),
('திவெ22:4-5', ''),
('திவெ7:10,12', 'அவர்கள், “அரியணையில் வீற்றிருக்கும் எங்கள் கடவுளிடமிருந்தும், ஆட்டுக் குட்டியிடமிருந்துமே மீட்பு வருகிறது” என்று உரத்த குரலில் பாடினார்கள். “புகழ்ச்சியும் பெருமையும் ஞானமும் நன்றியும் மாண்பும் வல்லமையும் வலிமையும் எங்கள் கடவுளுக்கே என்றென்றும் உரியன; ஆமென்”, என்று பாடினார்கள்.'),
('திவெ8:3-4', ''),
('தொநூ28:12-13', ''),
('தோபி4:15அ,16அ,18அ,19அ', 'உனக்குப் பிடிக்காத எதையும் பிறருக்குச் செய்யாதே. உன் உணவில் ஒரு பகுதியை பசித்திருப்போருக்கு வழங்கு. ஞானிகளிடம் அறிவுரை கேள். எல்லாக் காலத்திலும் உன் கடவுளாகிய ஆண்டவரைப் போற்று. உன் வழிகள் நேரியவையாய் அமையவும் உம் முயற்சிகளும் திட்டங்களும் வெற்றியடையவும் அவரிடம் மன்றாடு.'),
('நீமொ3:13-15', 'ஞானத்தைத் தேடி அடைந்தோர் நற்பேறு பெற்றோர்: மெய்யறிவை அடைந்தோர் நற்பேறு பெற்றோர்: வெள்ளியைவிட ஞானமே மிகுநலன் தருவது; பொன்னைவிட ஞானத்தால் வரும் செல்வம் மேலானது. ஞானம் பவளத்தைவிட விலைமதிப்புள்ளது. உன் அரும்பொருள் எதுவும் அதற்கு நிகராகாது.'),
('பாரூக்4:28-29', 'கடவுளைவிட்டு அகன்று செல்வதில் முன்பு நீங்கள் முனைந்து நின்றீர்கள். அதைவிடப் பன்மடங்கு ஆர்வத்துடன் அவரைத் தேடும் பொருட்டு இப்பொழுது அவரிடம் திரும்பி வாருங்கள். ஏனெனில், இக்கேடுகளை உங்கள் மீது வரச்செய்தவரே உங்களுக்கு முடிவில்லா மகிழ்ச்சியையும் மீட்பையும் அருள்வார்.'),
('பிலி2:14-15', 'முணுமுணுக்காமலும் வாதாடாமலும் யாவற்றையும் செய்யுங்கள். அப்பொழுதுதான் நீங்கள் நெறிகெட்ட சீரழிந்த தலைமுறையினரிடயே குற்றமும் கபடுமற்றவர்களாய்க் கடவுளின் மாசற்ற குழந்தைகளெனத் திகழ்வீர்கள்; உலகில் ஒளிரும் சுடர்களாகத் துலங்குவீர்கள்.'),
('யாக்1:19.20,26', 'என் அன்பார்ந்த சகோதர சகோதரிகளே, இதைத் தெரிந்துகொள்ளுங்கள்; ஒவ்வொருவரும் கேட்பதில் வேகமும், பேசுவதிலும் சினங்கொள்வதிலும் தாமதமும் காட்டவேண்டும். ஏனெனில், மனிதரின் சினம் கடவுளுக்கு ஏற்புடைய செயல்கள் நிறைவேறத் தடையாயிருக்கிறது. தாம் சமயப்பற்றுடையோர் என எண்ணிக் கொண்டிருப்போர் தம் நாவை அடக்காமலிருப்பாரென்றால் தம்மையே ஏமாற்றிக் கொள்வர். இத்தகையோருடைய சமயப்பற்றுப் பயனற்றது.'),
('யாக்1:2-4', 'என் சகோதர சகோதரிகளே, பல வகையான சோதனைகளுக்கு உள்ளாகும்போது நீங்கள் மகிழ்ச்சியாய் இருக்க வேண்டும் என்பதைக் கருத்தில் கொண்டிருங்கள் உங்கள் நம்பிக்கை சோதிக்கப்படும்போது மனவுறுதி உண்டாகும் என்பது உங்களுக்குத் தெரியும். உங்கள் மனவுறுதி நிறைவான செயல்களால் விளங்கட்டும் அப்பொழுது எக்குறையுமின்றி முற்றும் நிறைவுள்ளவர்களாய் இருப்பீர்கள்.'),
('யாக்1:22,25', 'இறைவார்த்தையைக் கேட்கிறவர்களாக மட்டும் இருந்து உங்களை ஏமாற்றிக்கொள்ள வேண்டாம். அதன்படி நடக்கிறவர்களாயும் இருங்கள். ஆனால் நிறைவான விடுதலையளிக்கக் கூடிய சட்டத்தைக் கூர்ந்து கவனித்து, அதைத் தொடர்ந்து கற்போர், தாம் கேட்பதை மறந்துவிடுவதில்லை; அவர்கள் அதற்கேற்ற செயல்களைச் செய்வார்கள். தம் செயல்களால் பேறுபெற்றவர் ஆவார்கள்.'),
('யாக்2:12-13', 'விடுதலையளிக்கும் சட்டத்தின் தீர்ப்புக்கு உட்பட வேண்டியவர்களுக்கு ஏற்றதாய் உங்கள் பேச்சும் நடத்தையும் அமைதல் வேண்டும். ஏனெனில், இரக்கம் காட்டாதோருக்கு இரக்கமற்ற தீர்ப்புதான் கிடைக்கும். இரக்கமே தீர்ப்பை வெல்லும்.'),
('யாக்4:11-12', 'சகோதர சகோதரிகளே, உங்களுள் ஒருவர் மற்றவரைப் பழித்துரைக்க வேண்டாம். தம் சகோதரர் சகோதரிகளைப் பழித்துரைப்போர், அல்லது அவர்களுக்குத் தீர்ப்பு அளிப்போர் திருச்சட்டத்தைப் பழித்துரைக்கின்றனர்; அச்சட்டத்துக்கு எதிராகத் தீர்ப்பு அளிக்கின்றனர். சட்டத்துக்கு எதிராக நீங்கள் தீர்ப்பு அளிக்கும்போது நீங்கள் அதைக் கடைப்பிடிப்பவராக அல்ல, மாறாக அதற்கு எதிராகத் தீர்ப்பு அளிக்கும் நடுவர்களாக ஆகி வருகிறீர்கள். திருச்சட்டத்தைக் கொடுத்தவரும் தீர்ப்பு அளிப்பவரும் ஒருவரே. அவரே மீட்கவும் அழிக்கவும் வல்லவர். அவ்வாறிருக்க, உங்களை அடுத்திருப்பவருக்குத் தீர்ப்பளிக்க நீங்கள் யார்?'),
('யூதித்8:25-26', 'நம் கடவுனாகிய ஆண்டவருக்கு நன்றி செலுத்துவோம்; ஏனெனில், நம் மூதாதையரை அவர் சோதித்ததுபோல நம்மையும் சோதிக்கிறார். அவர் ஆபிரகாமுக்கு என்ன செய்தார் என்பதையும், யாக்கோபு தம் தாய்மாமன் லாபானின் ஆடுகளை வட மெசப்பொத்தாமியாவில் மேய்த்துக்கொண்டிருந்த போது அவருக்கு என்ன நிகழ்ந்தது என்பதையும் கண்ணிப்பாருங்கள்.'),
('யோபு1:21,2:10ஆ', '“என் தாயின் கருப்பையினின்று பிறந்த மேனியனாய் வான் வந்தேன்; அங்கே திரும்புகையில் பிறந்த மேனியனாய் யான் செல்வேன்; ஆண்டவர் அளித்தார்: ஆண்டவர் எடுத்துக் கொண்டார். ஆண்டவரது பெயர் போற்றப் பெறுக! நன்மையைக் கடவுளிடமிருந்து பெற்ற நாம் ஏன் தீமையைப் பெறக்கூடாது?”.'),
('லூக்1:47-55', 'ஆண்டவரை எனது உள்ளம்§போற்றிப் பெருமைப்படுத்துகின்றது. §என் மீட்பராம் கடவுளை நினைத்து§எனது மனம் பேருவகை கொள்கின்றது.§§ஏனெனில் அவர் தம் அடிமையின் தாழ்நிலையைக்§கண்ணோக்கினார்.§இதுமுதல் எல்லாத் தலைமுறையினரும்§என்னைப் பேறுபெற்றவர் என்பர்.§§ஏனெனில் வல்லவராம் கடவுள்§எக்கு அரும்பெரும் செயல்கள் செய்துள்ளார்.§தூயவர் என்பதே அவரது பெயர்.§§அவருக்கு அஞ்சி நடப்போருக்குத்§தலைமுறை தலைமுறையாய்§அவர் இரக்கம் காட்டி வருகிறார்.§§அவர் தம் தோள்வலிமையைக் காட்டியுள்ளார்§உள்ளத்தில் செருக்குடன் சிந்திப்போரைச்§சிதறடித்து வருகிறார்.§§வலியோரை அரியணையினின்று தூக்கிஎறிந்துள்ளார்;§தாழ்நிலையில் இருப்போரை உயர்த்துகிறார்.§§பசித்தோரை நலன்களால் நிரப்பியுள்ளார்§செல்வரை வெறுங்கையராய் அனுப்பிவிடுகிறார்.§§மூதாதையருக்கு உரைத்தபடியே§அவர் ஆபிரகாமையும் அவர்தம் வழிமரபினரையும்§என்றென்றும் இரக்கத்தோடு நினைவில்கொண்டுள்ளார்;§தம் ஊழியராகிய இஸ்ரயேலுக்குத்§துணையாக இருந்து வருகிறார்”.§§தந்தைக்கும், மகனுக்கும், தூய ஆவியாருக்கும் §மாட்சி உண்டாவதாக.§§தொடக்கத்திலே இருந்ததுபோல் இப்பொழுதும் எப்பொழுதும்§என்றென்றும் இருப்பதாக. ஆமென்.'),
('லூக்1:68-79', 'இஸ்ரயேலின் கடவுளாகிய§ஆண்டவரைப் போற்றுவோம்§எனெனில் அவர்தம் மக்களைத்§தேடிவந்து விடுவித்தருளினார்.§§தும் தூய இறைவாக்கினர் வாயினால்§தொடக்க முதல் அவர் மொழிந்தபடியே§அவர் தம் ஊழியராகிய தாவீதின் குடும்பத்தில்§வல்லமை உடைய மீட்பர் ஒருவர்§நமக்காகத் தோன்றச் செய்தார்;§நம் பகைவரிடமிருந்தும்§நம்மை வெறுப்போர் அனைவரின் பிடியிலிருந்தும்§நம்மை மீட்பார்.§§அவர் நம் மூதாதையருக்கு இரக்கம் காட்டி,§தமது தூய உடன்படிக்கையையும்,§நம் தந்தையாகிய ஆபிரகாமுக்கு§அவர் இட்ட ஆணையையும்§நிறைவேற்ற நினைவு கூர்ந்தார்.§இவ்வாறு நாம் பகைவரின் பிடியிலிருந்து§விடுவிக்கப்பட்டுத்§தூய்மையோடும் நேர்மையோடும்§வாழ்நாளெல்லாம் அச்சமின்றி §அவர் திருமுன் பணி செய்யுமாறு வழிவகுத்தார்.§§குழந்தாய்,§உன்னத கடவுளின் இறைவாக்கினர் எனப்படுவாய்;§ஏனெனில் பாவ மன்னிப்பால் வரும் மீட்பை§அவர்தம் மக்களுக்கு அறிவித்து§ஆண்டவருக்கான வழியைச் செம்மைப்படுத்த§அவர் முன்னே செல்வாய்.§§இருளிலும் இறப்பின் பிடியிலும் இருப்போர்க்கு§ஒளிதரவும்,§நம்முடைய கால்களை§அமைதி வழியில் நடக்கச் செய்யவும்§நம் கடவுளின் பரிவுள்ளத்தாலும் இரக்கத்தாலும்§விண்ணிலிருந்து விடியல் நம்மைத் தேடி வருகிறது.\"§§தந்தைக்கும், மகனுக்கும், தூய ஆவியாருக்கும் §மாட்சி உண்டாவதாக.§§தொடக்கத்திலே இருந்ததுபோல் இப்பொழுதும் எப்பொழுதும்§என்றென்றும் இருப்பதாக. ஆமென்.'),
('விப23:20-21', '');
